"use client"

import type React from "react"
import { useState, useEffect } from "react"
import {
  Info,
  Lightbulb,
  CircleAlert,
  TriangleAlert,
  CircleX,
  CircleCheck,
  ShieldAlert,
  X,
  ChevronDown,
} from "lucide-react"
import { cn } from "@/lib/utils"

const admonitionConfig = {
  note: {
    bgColor: "bg-blue-50 dark:bg-blue-950/30",
    borderColor: "border-blue-200 dark:border-blue-800",
    textColor: "text-blue-900 dark:text-blue-200",
    iconColor: "text-blue-600 dark:text-blue-400",
    icon: Info,
    selectionColor:
      "selection:bg-blue-200 selection:text-blue-900 dark:selection:bg-blue-800 dark:selection:text-blue-100",
  },
  tip: {
    bgColor: "bg-emerald-50 dark:bg-emerald-950/30",
    borderColor: "border-emerald-200 dark:border-emerald-800",
    textColor: "text-emerald-900 dark:text-emerald-200",
    iconColor: "text-emerald-600 dark:text-emerald-400",
    icon: Lightbulb,
    selectionColor:
      "selection:bg-emerald-200 selection:text-emerald-900 dark:selection:bg-emerald-800 dark:selection:text-emerald-100",
  },
  info: {
    bgColor: "bg-slate-50 dark:bg-slate-950/30",
    borderColor: "border-slate-200 dark:border-slate-800",
    textColor: "text-slate-700 dark:text-slate-300",
    iconColor: "text-slate-500 dark:text-slate-400",
    icon: CircleAlert,
    selectionColor:
      "selection:bg-slate-200 selection:text-slate-900 dark:selection:bg-slate-800 dark:selection:text-slate-100",
  },
  warning: {
    bgColor: "bg-yellow-50 dark:bg-yellow-950/30",
    borderColor: "border-yellow-300 dark:border-yellow-700",
    textColor: "text-yellow-900 dark:text-yellow-200",
    iconColor: "text-yellow-600 dark:text-yellow-400",
    icon: TriangleAlert,
    selectionColor:
      "selection:bg-yellow-200 selection:text-yellow-900 dark:selection:bg-yellow-800 dark:selection:text-yellow-100",
  },
  danger: {
    bgColor: "bg-red-50 dark:bg-red-950/30",
    borderColor: "border-red-200 dark:border-red-800",
    textColor: "text-red-900 dark:text-red-200",
    iconColor: "text-red-600 dark:text-red-400",
    icon: CircleX,
    selectionColor: "selection:bg-red-200 selection:text-red-900 dark:selection:bg-red-800 dark:selection:text-red-100",
  },
  success: {
    bgColor: "bg-green-50 dark:bg-green-950/30",
    borderColor: "border-green-200 dark:border-green-800",
    textColor: "text-green-900 dark:text-green-200",
    iconColor: "text-green-600 dark:text-green-400",
    icon: CircleCheck,
    selectionColor:
      "selection:bg-green-200 selection:text-green-900 dark:selection:bg-green-800 dark:selection:text-green-100",
  },
  caution: {
    bgColor: "bg-orange-50 dark:bg-orange-950/30",
    borderColor: "border-orange-200 dark:border-orange-700",
    textColor: "text-orange-900 dark:text-orange-200",
    iconColor: "text-orange-600 dark:text-orange-400",
    icon: ShieldAlert,
    selectionColor:
      "selection:bg-orange-200 selection:text-orange-900 dark:selection:bg-orange-800 dark:selection:text-orange-100",
  },
}

const sizeVariants = {
  sm: {
    container: "p-3",
    title: "text-sm font-medium",
    content: "text-sm",
    icon: "h-4 w-4",
  },
  small: {
    container: "p-3",
    title: "text-sm font-medium",
    content: "text-sm",
    icon: "h-4 w-4",
  },
  default: {
    container: "p-4",
    title: "text-base font-medium",
    content: "text-sm",
    icon: "h-5 w-5",
  },
  lg: {
    container: "p-5",
    title: "text-base font-medium",
    content: "text-base",
    icon: "h-5 w-5",
  },
  large: {
    container: "p-5",
    title: "text-base font-medium",
    content: "text-base",
    icon: "h-5 w-5",
  },
}

const layoutVariants = {
  inline: "max-w-fit",
  "full-width": "w-full",
}

interface AdmonitionAction {
  label: string
  onClick: () => void
  variant?: "primary" | "secondary"
}

interface AdmonitionProps {
  type?: keyof typeof admonitionConfig
  title?: string
  children: React.ReactNode
  icon?: React.ComponentType<{ className?: string }>
  className?: string
  dismissible?: boolean
  dismissKey?: string
  collapsible?: boolean
  defaultCollapsed?: boolean
  actions?: AdmonitionAction[]
  size?: keyof typeof sizeVariants
  layout?: keyof typeof layoutVariants
}

export function Admonition({
  type = "note",
  title,
  children,
  icon: CustomIcon,
  className = "",
  dismissible = false,
  dismissKey,
  collapsible = false,
  defaultCollapsed = false,
  actions,
  size = "default",
  layout = "full-width",
}: AdmonitionProps) {
  const config = admonitionConfig[type]
  const IconComponent = CustomIcon || config.icon

  const [isDismissed, setIsDismissed] = useState(false)

  const [isCollapsed, setIsCollapsed] = useState(defaultCollapsed)

  useEffect(() => {
    if (dismissible && dismissKey) {
      const dismissed = localStorage.getItem(`admonition-dismissed-${dismissKey}`)
      if (dismissed === "true") {
        setIsDismissed(true)
      }
    }
  }, [dismissible, dismissKey])

  const handleDismiss = () => {
    setIsDismissed(true)
    if (dismissKey) {
      localStorage.setItem(`admonition-dismissed-${dismissKey}`, "true")
    }
  }

  if (isDismissed) {
    return null
  }

  return (
    <div
      data-slot="admonition"
      className={cn(
        "rounded-md border",
        sizeVariants[size].container,
        layoutVariants[layout],
        config.bgColor,
        config.borderColor,
        config.selectionColor,
        className,
      )}
    >
      <div className="flex items-start gap-3">
        <div className={cn(config.iconColor, "flex h-[1lh] items-center flex-shrink-0")}>
          <IconComponent className={sizeVariants[size].icon} />
        </div>
        <div className="min-w-0 flex-1">
          {title && <div className={cn(config.textColor, sizeVariants[size].title)}>{title}</div>}
          {(collapsible || dismissible) && (
            <div className="flex items-center gap-1 -mt-6 float-right">
              {collapsible && (
                <button
                  onClick={() => setIsCollapsed(!isCollapsed)}
                  className={cn(
                    config.iconColor,
                    "rounded p-1 transition-transform hover:bg-black/5 dark:hover:bg-white/5",
                    isCollapsed && "rotate-180",
                  )}
                  aria-label={isCollapsed ? "Expand" : "Collapse"}
                >
                  <ChevronDown className="h-5 w-5" />
                </button>
              )}
              {dismissible && (
                <button
                  onClick={handleDismiss}
                  className={cn(
                    config.iconColor,
                    "rounded p-1 transition-colors hover:bg-black/5 dark:hover:bg-white/5",
                  )}
                  aria-label="Dismiss"
                >
                  <X className="h-5 w-5" />
                </button>
              )}
            </div>
          )}
          {!isCollapsed && (
            <>
              <div className={cn(config.textColor, "mt-1 leading-relaxed", sizeVariants[size].content)}>{children}</div>
              {actions && actions.length > 0 && (
                <div className="mt-3 flex flex-wrap gap-2">
                  {actions.map((action, index) => (
                    <button
                      key={index}
                      onClick={action.onClick}
                      className={cn(
                        "rounded-md px-3 py-1.5 text-sm font-medium transition-colors",
                        action.variant === "primary"
                          ? cn(
                              type === "note" && "bg-blue-600 text-white hover:bg-blue-700 dark:bg-blue-500",
                              type === "tip" && "bg-emerald-600 text-white hover:bg-emerald-700 dark:bg-emerald-500",
                              type === "info" && "bg-slate-600 text-white hover:bg-slate-700 dark:bg-slate-500",
                              type === "warning" && "bg-yellow-600 text-white hover:bg-yellow-700 dark:bg-yellow-500",
                              type === "danger" && "bg-red-600 text-white hover:bg-red-700 dark:bg-red-500",
                              type === "success" && "bg-green-600 text-white hover:bg-green-700 dark:bg-green-500",
                              type === "caution" && "bg-orange-600 text-white hover:bg-orange-700 dark:bg-orange-500",
                            )
                          : cn(config.textColor, "border border-current hover:bg-black/5 dark:hover:bg-white/5"),
                      )}
                    >
                      {action.label}
                    </button>
                  ))}
                </div>
              )}
            </>
          )}
        </div>
      </div>
    </div>
  )
}
