import * as React from "react"
import { NavigationMenu as NavigationMenuPrimitive } from "@base-ui-components/react/navigation-menu"
import { cva } from "class-variance-authority"
import { ChevronDownIcon } from "lucide-react"

import { cn } from "@/lib/utils"

function NavigationMenu({
  className,
  children,
  ...props
}: React.ComponentProps<typeof NavigationMenuPrimitive.Root>) {
  return (
    <NavigationMenuPrimitive.Root
      data-slot="navigation-menu"
      className={cn(
        "group/navigation-menu relative flex max-w-max flex-1 items-center justify-center",
        className
      )}
      {...props}
    >
      {children}
    </NavigationMenuPrimitive.Root>
  )
}

function NavigationMenuList({
  className,
  ...props
}: React.ComponentProps<typeof NavigationMenuPrimitive.List>) {
  return (
    <NavigationMenuPrimitive.List
      data-slot="navigation-menu-list"
      className={cn(
        "group flex flex-1 list-none items-center justify-center gap-1",
        className
      )}
      {...props}
    />
  )
}

function NavigationMenuItem({
  className,
  ...props
}: React.ComponentProps<typeof NavigationMenuPrimitive.Item>) {
  return (
    <NavigationMenuPrimitive.Item
      data-slot="navigation-menu-item"
      className={cn("relative", className)}
      {...props}
    />
  )
}

const navigationMenuTriggerStyle = cva(
  "group inline-flex h-9 w-max items-center justify-center rounded-md bg-background px-4 py-2 text-sm font-medium hover:bg-accent hover:text-accent-foreground focus:bg-accent focus:text-accent-foreground disabled:pointer-events-none disabled:opacity-50 data-[popup-open]:hover:bg-accent data-[popup-open]:text-accent-foreground data-[popup-open]:focus:bg-accent data-[popup-open]:bg-accent/50 focus-visible:ring-ring/50 outline-none transition-[color,box-shadow] focus-visible:ring-[3px] focus-visible:outline-1"
)

function NavigationMenuTrigger({
  className,
  children,
  ...props
}: React.ComponentProps<typeof NavigationMenuPrimitive.Trigger>) {
  return (
    <NavigationMenuPrimitive.Trigger
      data-slot="navigation-menu-trigger"
      className={cn(navigationMenuTriggerStyle(), "group", className)}
      {...props}
    >
      {children}{" "}
      <ChevronDownIcon
        className="relative top-[1px] ml-1 size-3 transition duration-300 group-data-[popup-open]:rotate-180"
        aria-hidden="true"
      />
    </NavigationMenuPrimitive.Trigger>
  )
}

function NavigationMenuContent({
  className,
  ...props
}: React.ComponentProps<typeof NavigationMenuPrimitive.Content>) {
  return (
    <NavigationMenuPrimitive.Content
      data-slot="navigation-menu-content"
      className={cn(
        "xs:min-w-[400px] h-full w-[calc(100vw_-_40px)] p-2 sm:w-max",
        "transition-[opacity,transform,translate] duration-[var(--duration)] ease-[var(--easing)]",
        "data-[ending-style]:opacity-0 data-[starting-style]:opacity-0",
        "data-[starting-style]:data-[activation-direction=left]:translate-x-[-50%]",
        "data-[starting-style]:data-[activation-direction=right]:translate-x-[50%]",
        "data-[ending-style]:data-[activation-direction=left]:translate-x-[50%]",
        "data-[ending-style]:data-[activation-direction=right]:translate-x-[-50%]",
        className
      )}
      {...props}
    />
  )
}

function NavigationMenuPositioner({
  className,
  children,
  ...props
}: React.ComponentProps<typeof NavigationMenuPrimitive.Positioner>) {
  return (
    <NavigationMenuPrimitive.Portal>
      <NavigationMenuPrimitive.Positioner
        data-slot="navigation-menu-positioner"
        sideOffset={10}
        collisionPadding={{ top: 5, bottom: 5, left: 20, right: 20 }}
        collisionAvoidance={{ side: "none" }}
        className={cn(
          "box-border h-[var(--positioner-height)] w-[var(--positioner-width)] max-w-[var(--available-width)] transition-[top,left,right,bottom] duration-[var(--duration)] ease-[var(--easing)] before:absolute before:content-[''] data-[instant]:transition-none data-[side=bottom]:before:top-[-10px] data-[side=bottom]:before:right-0 data-[side=bottom]:before:left-0 data-[side=bottom]:before:h-2.5 data-[side=left]:before:top-0 data-[side=left]:before:right-[-10px] data-[side=left]:before:bottom-0 data-[side=left]:before:w-2.5 data-[side=right]:before:top-0 data-[side=right]:before:bottom-0 data-[side=right]:before:left-[-10px] data-[side=right]:before:w-2.5 data-[side=top]:before:right-0 data-[side=top]:before:bottom-[-10px] data-[side=top]:before:left-0 data-[side=top]:before:h-2.5",
          className
        )}
        style={
          {
            "--duration": "0.35s",
            "--easing": "cubic-bezier(0.22, 1, 0.36, 1)",
          } as React.CSSProperties
        }
        {...props}
      >
        {children}
      </NavigationMenuPrimitive.Positioner>
    </NavigationMenuPrimitive.Portal>
  )
}

function NavigationMenuPopup({
  className,
  children,
  ...props
}: React.ComponentProps<typeof NavigationMenuPrimitive.Popup>) {
  return (
    <NavigationMenuPrimitive.Popup
      className={cn(
        "data-[ending-style]:easing-[ease] bg-popover outline-border xs:w-[var(--popup-width)] relative h-[var(--popup-height)] w-[var(--popup-width)] origin-[var(--transform-origin)] rounded-lg shadow-lg outline-1 transition-[opacity,transform,width,height,scale,translate] duration-[var(--duration)] ease-[var(--easing)] data-[ending-style]:scale-90 data-[ending-style]:opacity-0 data-[ending-style]:duration-150 data-[starting-style]:scale-90 data-[starting-style]:opacity-0 dark:shadow-none dark:-outline-offset-1",
        className
      )}
      {...props}
    >
      {children}
      <NavigationMenuViewport />
    </NavigationMenuPrimitive.Popup>
  )
}

function NavigationMenuViewport({
  className,
  ...props
}: React.ComponentProps<typeof NavigationMenuPrimitive.Viewport>) {
  return (
    <NavigationMenuPrimitive.Viewport
      className={cn("relative h-full w-full overflow-hidden", className)}
      {...props}
    />
  )
}

function NavigationMenuLink({
  className,
  ...props
}: React.ComponentProps<typeof NavigationMenuPrimitive.Link>) {
  return (
    <NavigationMenuPrimitive.Link
      data-slot="navigation-menu-link"
      className={cn(
        "data-[active]:focus:bg-accent data-[active]:hover:bg-accent data-[active]:bg-accent/50 data-[active]:text-accent-foreground hover:bg-accent hover:text-accent-foreground focus:bg-accent focus:text-accent-foreground focus-visible:ring-ring/50 [&_svg:not([class*='text-'])]:text-muted-foreground flex flex-col gap-1 rounded-sm p-2 text-sm transition-all outline-none focus-visible:ring-[3px] focus-visible:outline-1 [&_svg:not([class*='size-'])]:size-4",
        className
      )}
      {...props}
    />
  )
}

function NavigationMenuArrow({
  className,
  ...props
}: React.ComponentProps<typeof NavigationMenuPrimitive.Arrow>) {
  return (
    <NavigationMenuPrimitive.Arrow
      data-slot="navigation-menu-arrow"
      className={cn(
        "flex transition-[left] duration-[var(--duration)] ease-[var(--easing)] data-[side=bottom]:top-[-8px] data-[side=left]:right-[-13px] data-[side=left]:rotate-90 data-[side=right]:left-[-13px] data-[side=right]:-rotate-90 data-[side=top]:bottom-[-8px] data-[side=top]:rotate-180",
        className
      )}
      {...props}
    >
      <ArrowSvg />
    </NavigationMenuPrimitive.Arrow>
  )
}

function ArrowSvg(props: React.ComponentProps<"svg">) {
  return (
    <svg width="20" height="10" viewBox="0 0 20 10" fill="none" {...props}>
      <path
        d="M9.66437 2.60207L4.80758 6.97318C4.07308 7.63423 3.11989 8 2.13172 8H0V10H20V8H18.5349C17.5468 8 16.5936 7.63423 15.8591 6.97318L11.0023 2.60207C10.622 2.2598 10.0447 2.25979 9.66437 2.60207Z"
        className="fill-popover"
      />
      <path
        d="M8.99542 1.85876C9.75604 1.17425 10.9106 1.17422 11.6713 1.85878L16.5281 6.22989C17.0789 6.72568 17.7938 7.00001 18.5349 7.00001L15.89 7L11.0023 2.60207C10.622 2.2598 10.0447 2.2598 9.66436 2.60207L4.77734 7L2.13171 7.00001C2.87284 7.00001 3.58774 6.72568 4.13861 6.22989L8.99542 1.85876Z"
        className="fill-border dark:fill-none"
      />
      <path
        d="M10.3333 3.34539L5.47654 7.71648C4.55842 8.54279 3.36693 9 2.13172 9H0V8H2.13172C3.11989 8 4.07308 7.63423 4.80758 6.97318L9.66437 2.60207C10.0447 2.25979 10.622 2.2598 11.0023 2.60207L15.8591 6.97318C16.5936 7.63423 17.5468 8 18.5349 8H20V9H18.5349C17.2998 9 16.1083 8.54278 15.1901 7.71648L10.3333 3.34539Z"
        className="dark:fill-border"
      />
    </svg>
  )
}

export {
  NavigationMenu,
  NavigationMenuList,
  NavigationMenuItem,
  NavigationMenuContent,
  NavigationMenuTrigger,
  NavigationMenuLink,
  NavigationMenuPositioner,
  NavigationMenuViewport,
  navigationMenuTriggerStyle,
  NavigationMenuPopup,
  NavigationMenuArrow,
}

"use client"

import * as React from "react"
import { useEffect, useState } from "react"
import { TerminalIcon } from "lucide-react"
import { Highlight, Prism } from "prism-react-renderer"
import type { PrismTheme } from "prism-react-renderer"

// Import additional language definitions

import { cn } from "@/lib/utils"
import { CopyButton } from "@/registry/delta-ui/delta/copy-button"
import {
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
} from "@/registry/delta-ui/delta/tabs"

;(typeof global !== "undefined" ? global : window).Prism = Prism
require("prismjs/components/prism-cpp")
require("prismjs/components/prism-python")
require("prismjs/components/prism-bash")
require("prismjs/components/prism-typescript")
require("prismjs/components/prism-javascript")
require("prismjs/components/prism-jsx")
require("prismjs/components/prism-tsx")
require("prismjs/components/prism-go")
require("prismjs/components/prism-rust")

type PackageManager = "npm" | "yarn" | "pnpm" | "bun"

// Inline theme definitions
const defaultTheme: PrismTheme = {
  plain: {
    color: "#FFFFFF",
    backgroundColor: "#161616",
  },
  styles: [
    {
      types: ["comment"],
      style: {
        color: "#757575",
        fontStyle: "italic",
      },
    },
    {
      types: ["keyword", "property", "property-access", "attr-name"],
      style: {
        color: "#77b7d7",
      },
    },
    {
      types: ["tag"],
      style: {
        color: "#dfab5c",
      },
    },
    {
      types: ["punctuation", "symbol", "dom"],
      style: {
        color: "#ffffff",
      },
    },
    {
      types: ["definition", "function"],
      style: {
        color: "#86d9ca",
      },
    },
    {
      types: ["string", "char", "attr-value"],
      style: {
        color: "#977cdc",
      },
    },
    {
      types: ["static", "number"],
      style: {
        color: "#ff6658",
      },
    },
  ],
}

const lightTheme: PrismTheme = {
  plain: {
    color: "#24292e",
    backgroundColor: "#F9F9F9",
  },
  styles: [
    {
      types: ["comment"],
      style: {
        color: "#8b949e",
        fontStyle: "italic",
      },
    },
    {
      types: ["variable", "parameter"],
      style: {
        color: "#e36209",
      },
    },
    {
      types: ["keyword", "builtin", "function-definition"],
      style: {
        color: "#d73a49",
      },
    },
    {
      types: ["property", "property-access", "attr-name"],
      style: {
        color: "#005cc5",
      },
    },
    {
      types: ["tag"],
      style: {
        color: "#22863a",
      },
    },
    {
      types: ["punctuation", "symbol", "dom", "operator"],
      style: {
        color: "#24292e",
      },
    },
    {
      types: ["function"],
      style: {
        color: "#6f42c1",
      },
    },
    {
      types: ["class-name"],
      style: {
        color: "#6f42c1",
      },
    },
    {
      types: ["string", "char", "attr-value"],
      style: {
        color: "#032f62",
      },
    },
    {
      types: ["static", "number"],
      style: {
        color: "#005cc5",
      },
    },
  ],
}

interface CodeBlockProps {
  // Package manager commands
  npm?: string
  yarn?: string
  pnpm?: string
  bun?: string
  defaultPackageManager?: PackageManager

  // Code highlighting props
  code?: string
  css?: string
  language?: string
  defaultLanguage?: string
  filename?: string
  showLineNumbers?: boolean
  theme?: PrismTheme
  adaptiveTheme?: {
    light: PrismTheme
    dark: PrismTheme
  }

  // Background options
  useThemeBackground?: boolean // When true, uses the JSON theme background; when false, uses bg-surface

  className?: string
}

export function CodeBlock({
  npm,
  yarn,
  pnpm,
  bun,
  defaultPackageManager = "npm",
  code,
  css,
  language = "typescript",
  defaultLanguage,
  filename,
  showLineNumbers = true,
  theme,
  adaptiveTheme,
  useThemeBackground = false,
  className,
}: CodeBlockProps) {
  const [packageManager, setPackageManager] = React.useState<PackageManager>(
    defaultPackageManager
  )
  const [isDark, setIsDark] = useState(false)

  // Hook to detect dark mode
  useEffect(() => {
    const checkDarkMode = () => {
      setIsDark(document.documentElement.classList.contains("dark"))
    }

    checkDarkMode()

    const observer = new MutationObserver((mutations) => {
      mutations.forEach((mutation) => {
        if (
          mutation.type === "attributes" &&
          mutation.attributeName === "class"
        ) {
          checkDarkMode()
        }
      })
    })

    observer.observe(document.documentElement, {
      attributes: true,
      attributeFilter: ["class"],
    })

    return () => observer.disconnect()
  }, [])

  const commands = React.useMemo(() => {
    return {
      npm,
      yarn,
      pnpm,
      bun,
    }
  }, [npm, yarn, pnpm, bun])

  const availableCommands = Object.entries(commands).filter(
    ([, command]) => command
  )

  // Select theme based on adaptiveTheme prop or auto-detect light/dark mode
  const selectedTheme = adaptiveTheme
    ? isDark
      ? adaptiveTheme.dark
      : adaptiveTheme.light
    : theme || (isDark ? defaultTheme : lightTheme)

  // Use the theme's text foreground color for the copy button
  const iconColor = selectedTheme.plain?.color || "#000000"

  // Handle CSS prop - treat as code with CSS language
  const actualCode = css || code
  const actualLanguage = css ? "css" : defaultLanguage || language

  // If we have package manager commands, show the tabbed interface
  if (availableCommands.length > 0) {
    return (
      <div
        className={cn(
          "bg-card text-card-foreground overflow-x-auto rounded-lg border",
          className
        )}
        data-slot="tabs"
      >
        <Tabs
          value={packageManager}
          className="gap-0"
          onValueChange={(value) => setPackageManager(value as PackageManager)}
        >
          <div
            className={cn(
              "flex items-center justify-between border-b px-3 py-1",
              !useThemeBackground && "bg-surface"
            )}
            style={
              useThemeBackground
                ? {
                    backgroundColor: selectedTheme.plain?.backgroundColor,
                  }
                : undefined
            }
          >
            <div className="flex items-center gap-2">
              <div className="bg-foreground flex size-4 items-center justify-center rounded-[1px] opacity-70">
                <TerminalIcon className="text-code size-3" />
              </div>
              <TabsList className="rounded-none bg-transparent p-0">
                {availableCommands.map(([key]) => (
                  <TabsTrigger
                    key={key}
                    value={key}
                    className="data-[state=active]:border-border h-7 rounded-sm border border-transparent pt-0.5 data-[state=active]:bg-transparent data-[state=active]:shadow-none"
                  >
                    {key}
                  </TabsTrigger>
                ))}
              </TabsList>
            </div>
            <CopyButton
              value={commands[packageManager] || ""}
              className="size-7 opacity-70 hover:opacity-100 focus-visible:opacity-100"
              tooltip="Copy command"
            />
          </div>
          <div className="no-scrollbar overflow-x-auto">
            {availableCommands.map(([key, command]) => (
              <TabsContent key={key} value={key} className="mt-0">
                <div
                  className={cn(
                    "relative py-4",
                    !useThemeBackground && "bg-surface"
                  )}
                  style={
                    useThemeBackground
                      ? {
                          backgroundColor: selectedTheme.plain?.backgroundColor,
                        }
                      : undefined
                  }
                >
                  <Highlight
                    theme={selectedTheme}
                    code={command || ""}
                    language="bash"
                  >
                    {({
                      className: highlightClassName,
                      style,
                      tokens,
                      getLineProps,
                      getTokenProps,
                    }) => (
                      <pre
                        className={`${highlightClassName} w-full overflow-x-auto px-4 font-mono text-sm leading-relaxed font-medium`}
                        style={{
                          ...style,
                          backgroundColor: useThemeBackground
                            ? selectedTheme.plain?.backgroundColor
                            : "transparent",
                          fontSize: className?.includes("text-lg")
                            ? "1.125rem"
                            : className?.includes("text-base")
                              ? "1rem"
                              : undefined,
                        }}
                      >
                        {tokens.map((line, i) => (
                          <div key={i} {...getLineProps({ line })}>
                            {line.map((token, key) => (
                              <span key={key} {...getTokenProps({ token })} />
                            ))}
                          </div>
                        ))}
                      </pre>
                    )}
                  </Highlight>
                </div>
              </TabsContent>
            ))}
          </div>
        </Tabs>
      </div>
    )
  }

  // If we have code to highlight, show the syntax highlighted version
  if (actualCode) {
    return (
      <div
        className={cn(
          "border-border pointer-events-auto w-full max-w-full overflow-x-auto overflow-y-hidden rounded-sm border",
          className
        )}
      >
        {filename && (
          <div
            className={cn(
              "flex items-center justify-between border-b",
              !useThemeBackground && "bg-surface"
            )}
            style={
              useThemeBackground
                ? {
                    backgroundColor: selectedTheme.plain?.backgroundColor,
                  }
                : undefined
            }
          >
            <div className="flex items-center gap-2 px-3 py-1">
              <span className="text-sm font-medium">{filename}</span>
            </div>
            <CopyButton
              value={actualCode}
              className="mr-3"
              tooltip="Copy code"
            />
          </div>
        )}

        <div
          className={cn(
            "relative max-h-[calc(530px-44px)] w-full py-4",
            !useThemeBackground && "bg-surface"
          )}
          style={
            useThemeBackground
              ? {
                  backgroundColor: selectedTheme.plain?.backgroundColor,
                }
              : undefined
          }
        >
          {!filename && (
            <div className="absolute top-1 right-1">
              <CopyButton value={actualCode} tooltip="Copy code" />
            </div>
          )}

          <Highlight
            theme={selectedTheme}
            code={actualCode.trim()}
            language={actualLanguage}
          >
            {({
              className: highlightClassName,
              style,
              tokens,
              getLineProps,
              getTokenProps,
            }) => (
              <pre
                className={`${highlightClassName} thin-scrollbar max-h-[calc(530px-88px)] w-full overflow-x-auto overflow-y-auto font-mono text-sm leading-relaxed font-medium`}
                style={{
                  ...style,
                  backgroundColor: useThemeBackground
                    ? selectedTheme.plain?.backgroundColor
                    : "transparent",
                  fontSize: className?.includes("text-lg")
                    ? "1.125rem"
                    : className?.includes("text-base")
                      ? "1rem"
                      : undefined,
                  scrollbarWidth: "thin",
                  scrollbarColor:
                    selectedTheme.plain?.backgroundColor?.toLowerCase() ===
                      "#ffffff" ||
                    selectedTheme.plain?.backgroundColor === "#FAFAFA"
                      ? "#d1d5db transparent"
                      : "#4b5563 transparent",
                }}
              >
                {tokens.map((line, i) => (
                  <div
                    key={i}
                    {...getLineProps({ line })}
                    className="flex items-center px-4 py-px"
                  >
                    {showLineNumbers && (
                      <span
                        className="mr-4 flex items-center text-right text-sm select-none"
                        style={{
                          color:
                            selectedTheme.plain?.backgroundColor ===
                              "#FFFFFF" ||
                            selectedTheme.plain?.backgroundColor === "#FAFAFA"
                              ? "#999999"
                              : "#757575",
                          minWidth: "1.5rem",
                          fontSize: className?.includes("text-lg")
                            ? "1.125rem"
                            : className?.includes("text-base")
                              ? "1rem"
                              : undefined,
                        }}
                      >
                        {i + 1}
                      </span>
                    )}
                    <span className={!showLineNumbers ? "ml-0" : ""}>
                      {line.map((token, key) => (
                        <span key={key} {...getTokenProps({ token })} />
                      ))}
                    </span>
                  </div>
                ))}
              </pre>
            )}
          </Highlight>
        </div>
      </div>
    )
  }

  // Return null if no content
  return null
}

"use client"

import * as React from "react"
import { CheckIcon, CopyIcon } from "lucide-react"

import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from "@/components/ui/tooltip"

export function copyToClipboard(value: string) {
  navigator.clipboard.writeText(value)
}

interface CopyButtonProps extends React.ComponentProps<typeof Button> {
  value: string
  variant?:
    | "ghost"
    | "outline"
    | "secondary"
    | "default"
    | "destructive"
    | "link"
  iconColor?: string
  tooltip?: string
}

export function CopyButton({
  value,
  className,
  variant = "ghost",
  iconColor,
  tooltip = "Copy to Clipboard",
  ...props
}: CopyButtonProps) {
  const [hasCopied, setHasCopied] = React.useState(false)

  React.useEffect(() => {
    if (hasCopied) {
      const timer = setTimeout(() => {
        setHasCopied(false)
      }, 2000)
      return () => clearTimeout(timer)
    }
  }, [hasCopied])

  return (
    <Tooltip>
      <TooltipTrigger asChild>
        <Button
          data-slot="copy-button"
          size="icon"
          variant={variant}
          className={cn(
            "size-7 hover:opacity-100 focus-visible:opacity-100",
            className
          )}
          onClick={() => {
            copyToClipboard(value)
            setHasCopied(true)
          }}
          {...props}
        >
          <span className="sr-only">Copy</span>
          <div className="relative">
            <div
              className={cn(
                "absolute inset-0 flex items-center justify-center transition-all duration-300 ease-in-out will-change-[transform,opacity,filter]",
                hasCopied
                  ? "blur-0 scale-100 opacity-100"
                  : "scale-[0.25] opacity-0 blur-xs"
              )}
            >
              <CheckIcon
                className="h-3.5 w-3.5"
                style={iconColor ? { color: iconColor } : {}}
              />
            </div>
            <div
              className={cn(
                "transition-[transform,opacity,filter] duration-300 ease-in-out will-change-[transform,opacity,filter]",
                hasCopied
                  ? "scale-[0.25] opacity-0 blur-xs"
                  : "blur-0 scale-100 opacity-100"
              )}
            >
              <CopyIcon
                className="h-3.5 w-3.5"
                style={iconColor ? { color: iconColor } : {}}
              />
            </div>
          </div>
        </Button>
      </TooltipTrigger>
      <TooltipContent>{hasCopied ? "Copied" : tooltip}</TooltipContent>
    </Tooltip>
  )
}

"use client"

import { useEffect, useRef, useState } from "react"
import { Cambio } from "cambio"

import { cn } from "@/lib/utils"

type MotionPreset = "snappy" | "smooth" | "bouncy" | "reduced"

interface CambioImageProps {
  src: string
  alt: string
  width: number
  height: number
  loading?: "lazy" | "eager"
  index?: number
  motion?:
    | MotionPreset
    | {
        trigger?: MotionPreset
        popup?: MotionPreset
        backdrop?: MotionPreset
      }
  dismissible?: boolean
  className?: string
  draggable?: boolean
  enableInitialAnimation?: boolean
  dismissOnImageClick?: boolean
}

export function CambioImage({
  src,
  alt,
  width,
  height,
  loading = "lazy",
  index = 0,
  motion = "snappy",
  dismissible = true,
  className,
  draggable = false,
  enableInitialAnimation = true,
  dismissOnImageClick = false,
}: CambioImageProps) {
  const [isVisible, setIsVisible] = useState(false)

  /* NEW â€” track the zoom state */
  const [open, setOpen] = useState(false)
  const ref = useRef<HTMLDivElement>(null)

  useEffect(() => {
    if (!enableInitialAnimation) {
      setIsVisible(true)
      return
    }

    const io = new IntersectionObserver(
      ([e]) => {
        if (e.isIntersecting) {
          setIsVisible(true)
          io.disconnect()
        }
      },
      { threshold: 0.1 }
    )
    if (ref.current) {
      io.observe(ref.current)
    }
    return () => io.disconnect()
  }, [enableInitialAnimation])

  /* Lower z-index when open to stay behind the popup */
  const zIndex = open ? 50 : 10 + index

  return (
    <span
      ref={ref}
      className={cn(
        "relative inline-block w-full transition-all duration-500 ease-out",
        className
      )}
      style={{
        opacity: enableInitialAnimation ? (isVisible ? 1 : 0) : 1,
        filter: enableInitialAnimation
          ? isVisible
            ? "blur(0)"
            : "blur(4px)"
          : "blur(0)",
        zIndex,
      }}
    >
      {/* @ts-ignore */}
      <Cambio.Root
        motion={motion}
        dismissible={dismissible}
        open={open}
        onOpenChange={setOpen}
      >
        {/* @ts-ignore */}
        <Cambio.Trigger
          className={cn(
            "relative w-full overflow-hidden",
            !open && "cursor-zoom-in"
          )}
          style={{ pointerEvents: open ? "none" : "auto" }}
        >
          <img
            src={src}
            alt={alt}
            width={width}
            height={height}
            loading={loading}
            draggable={draggable}
            className={cn("h-auto w-full", className)}
            style={{ pointerEvents: "none" }}
          />
        </Cambio.Trigger>

        {/* @ts-ignore */}
        <Cambio.Portal>
          {/* @ts-ignore */}
          <Cambio.Backdrop
            motion="reduced"
            className="fixed inset-0 z-[100] bg-black/40"
          />
          {/* @ts-ignore */}
          <Cambio.Popup className="z-[101] w-full overflow-hidden md:w-[70%]">
            <img
              src={src}
              alt={alt}
              width={width}
              height={height}
              loading="eager"
              draggable={draggable}
              className={cn(
                "h-auto w-full object-contain",
                dismissOnImageClick && "cursor-zoom-out"
              )}
              style={{ pointerEvents: dismissOnImageClick ? "auto" : "none" }}
              onClick={dismissOnImageClick ? () => setOpen(false) : undefined}
            />
          </Cambio.Popup>
        </Cambio.Portal>
      </Cambio.Root>
    </span>
  )
}

