const mockResponses = [
  "Hello! I'm an AI assistant built with Delta Components. I can help you with coding questions, explain concepts, or just have a conversation. What would you like to talk about?",
  "That's a great question! Let me help you understand this concept better. The key thing to remember is that proper implementation requires careful consideration of the underlying principles and best practices in the field.",
  "I'd be happy to explain this topic in detail. From my understanding, there are several important factors to consider when approaching this problem. Let me break it down step by step for you.",
  "This is an interesting topic that comes up frequently. The solution typically involves understanding the core concepts and applying them in the right context. Here's what I recommend...",
  "Great choice of topic! This is something that many developers encounter. The approach I'd suggest is to start with the fundamentals and then build up to more complex scenarios.",
]

const Example = () => {
  const [model, setModel] = useState<string>(models[0].id)
  const [text, setText] = useState<string>("")
  const [useWebSearch, setUseWebSearch] = useState<boolean>(false)
  const [status, setStatus] = useState<
    "submitted" | "streaming" | "ready" | "error"
  >("ready")
  const [messages, setMessages] = useState<MessageType[]>(initialMessages)
  const [streamingMessageId, setStreamingMessageId] = useState<string | null>(
    null
  )
  const [touchedMessages, setTouchedMessages] = useState<Set<string>>(new Set())
  const shouldCancelRef = useRef<boolean>(false)
  const addMessageTimeoutRef = useRef<NodeJS.Timeout | null>(null)
  const textareaRef = useRef<HTMLTextAreaElement>(null)

  const stop = useCallback(() => {
    console.log("Stopping generation...")

    // Set cancellation flag
    shouldCancelRef.current = true

    // Clear timeout for adding assistant message
    if (addMessageTimeoutRef.current) {
      clearTimeout(addMessageTimeoutRef.current)
      addMessageTimeoutRef.current = null
    }

    setStatus("ready")
    setStreamingMessageId(null)
  }, [])

  const streamResponse = useCallback(
    async (messageId: string, content: string) => {
      setStatus("streaming")
      setStreamingMessageId(messageId)
      shouldCancelRef.current = false

      const words = content.split(" ")
      let currentContent = ""

      for (let i = 0; i < words.length; i++) {
        // Check if streaming should be cancelled
        if (shouldCancelRef.current) {
          setStatus("ready")
          setStreamingMessageId(null)
          return
        }

        currentContent += (i > 0 ? " " : "") + words[i]

        setMessages((prev) =>
          prev.map((msg) => {
            if (msg.versions.some((v) => v.id === messageId)) {
              return {
                ...msg,
                versions: msg.versions.map((v) =>
                  v.id === messageId ? { ...v, content: currentContent } : v
                ),
              }
            }
            return msg
          })
        )

        await new Promise((resolve) =>
          setTimeout(resolve, Math.random() * 100 + 50)
        )
      }

      setStatus("ready")
      setStreamingMessageId(null)
    },
    []
  )

  const handleMessageTouch = useCallback((messageKey: string) => {
    setTouchedMessages((prev) => new Set(prev).add(messageKey))
  }, [])

  const addUserMessage = useCallback(
    (
      content: string,
      files?: { name: string; url: string; type: string }[]
    ) => {
      const userMessage: MessageType = {
        key: `user-${Date.now()}`,
        from: "user",
        versions: [
          {
            id: `user-${Date.now()}`,
            content,
          },
        ],
        files,
        avatar: "https://patrickprunty.com/icon.webp",
        name: "User",
      }

      setMessages((prev) => [...prev, userMessage])

      addMessageTimeoutRef.current = setTimeout(() => {
        const assistantMessageId = `assistant-${Date.now()}`
        const randomResponse =
          mockResponses[Math.floor(Math.random() * mockResponses.length)]

        const assistantMessage: MessageType = {
          key: `assistant-${Date.now()}`,
          from: "assistant",
          versions: [
            {
              id: assistantMessageId,
              content: "",
            },
          ],
          avatar: "https://github.com/openai.png",
          name: "Assistant",
        }

        setMessages((prev) => [...prev, assistantMessage])
        streamResponse(assistantMessageId, randomResponse)
        addMessageTimeoutRef.current = null
      }, 500)
    },
    [streamResponse]
  )

  const handleSubmit = (message: PromptInputMessage) => {
    // If currently streaming or submitted, stop instead of submitting
    if (status === "streaming" || status === "submitted") {
      stop()
      return
    }

    const hasText = Boolean(message.text)
    const hasAttachments = Boolean(message.files?.length)

    if (!(hasText || hasAttachments)) {
      return
    }

    setStatus("submitted")

    let processedFiles:
      | { name: string; url: string; type: string }[]
      | undefined

    if (message.files?.length) {
      processedFiles = message.files.map((file) => ({
        name: "name" in file ? (file as any).name : "Unknown file",
        url: "url" in file ? (file as any).url : "",
        type: "type" in file ? (file as any).type : "application/octet-stream",
      }))

      toast.success("Files attached", {
        description: `${message.files.length} file(s) attached to message`,
      })
    }

    addUserMessage(message.text || "", processedFiles)
    setText("")
  }

  return (
    <ChatContainer>
      <Conversation>
        <ConversationContent>
          {messages.map(({ versions, ...message }) => {
            const assistantMessages = messages.filter(
              (m) => m.from === "assistant"
            )
            const isLastAssistantMessage =
              message.from === "assistant" &&
              assistantMessages.length > 0 &&
              assistantMessages[assistantMessages.length - 1].key ===
                message.key

            return (
              <Branch defaultBranch={0} key={message.key}>
                <BranchMessages>
                  {versions.map((version) => (
                    <Message
                      from={message.from}
                      key={`${message.key}-${version.id}`}
                      className={cn(
                        message.from === "user"
                          ? "items-end justify-end"
                          : undefined,
                        "group/message"
                      )}
                      onTouchStart={() => handleMessageTouch(message.key)}
                    >
                      <div>
                        {message.sources?.length && (
                          <Sources>
                            <SourcesTrigger count={message.sources.length} />
                            <SourcesContent>
                              {message.sources.map((source) => (
                                <Source
                                  href={source.href}
                                  key={source.href}
                                  title={source.title}
                                />
                              ))}
                            </SourcesContent>
                          </Sources>
                        )}
                        {message.reasoning && (
                          <Reasoning duration={message.reasoning.duration}>
                            <ReasoningTrigger />
                            <ReasoningContent>
                              {message.reasoning.content}
                            </ReasoningContent>
                          </Reasoning>
                        )}
                        <MessageContent
                          className={cn(
                            message.from === "assistant" ? "max-w-full" : "",
                            message.from === "user" && "ml-auto w-fit"
                          )}
                        >
                          {message.files?.length && (
                            <div className="mb-3 space-y-2">
                              {message.files.map((file, index) => (
                                <div key={index} className="max-w-sm">
                                  {file.type.startsWith("image/") ? (
                                    <div className="relative">
                                      <img
                                        src={file.url}
                                        alt={file.name}
                                        className="h-auto max-w-full rounded-lg"
                                        style={{ maxHeight: "300px" }}
                                      />
                                      <div className="absolute bottom-2 left-2 rounded bg-black/70 px-2 py-1 text-xs text-white">
                                        {file.name}
                                      </div>
                                    </div>
                                  ) : (
                                    <div className="bg-muted flex items-center gap-2 rounded-lg p-3">
                                      <div className="bg-primary/10 flex h-8 w-8 items-center justify-center rounded">
                                        ðŸ“„
                                      </div>
                                      <div className="min-w-0 flex-1">
                                        <div className="truncate text-sm font-medium">
                                          {file.name}
                                        </div>
                                        <div className="text-muted-foreground text-xs">
                                          {file.type}
                                        </div>
                                      </div>
                                    </div>
                                  )}
                                </div>
                              ))}
                            </div>
                          )}
                          {version.content && (
                            <div className="text-base leading-[1.65rem]">
                              <Response>{version.content}</Response>
                            </div>
                          )}
                        </MessageContent>
                        {message.from === "assistant" && (
                          <div className="flex items-center">
                            {status === "streaming" &&
                              streamingMessageId === version.id && (
                                <Loader
                                  size={16}
                                  className="text-muted-foreground ml-1"
                                />
                              )}
                            {status !== "streaming" &&
                              streamingMessageId !== version.id && (
                                <Actions
                                  position="left"
                                  className={cn(
                                    isLastAssistantMessage ||
                                      touchedMessages.has(message.key)
                                      ? "opacity-100"
                                      : "opacity-0 group-hover/message:opacity-100"
                                  )}
                                >
                                  <CopyAction
                                    value={version.content}
                                    tooltip="Copy message"
                                  />
                                  <Action
                                    tooltip="Regenerate response"
                                    onClick={() =>
                                      toast.info("Regenerating response...")
                                    }
                                  >
                                    <RotateCcwIcon className="h-4 w-4" />
                                  </Action>
                                  <Action
                                    tooltip="Good response"
                                    onClick={() =>
                                      toast.success("Feedback recorded")
                                    }
                                  >
                                    <ThumbsUpIcon className="h-4 w-4" />
                                  </Action>
                                  <Action
                                    tooltip="Poor response"
                                    onClick={() =>
                                      toast.success("Feedback recorded")
                                    }
                                  >
                                    <ThumbsDownIcon className="h-4 w-4" />
                                  </Action>
                                </Actions>
                              )}
                          </div>
                        )}
                      </div>
                    </Message>
                  ))}
                </BranchMessages>
                {versions.length > 1 && (
                  <BranchSelector from={message.from}>
                    <BranchPrevious />
                    <BranchPage />
                    <BranchNext />
                  </BranchSelector>
                )}
              </Branch>
            )
          })}
        </ConversationContent>
        <ConversationScrollButton />
      </Conversation>
      <div className="grid shrink-0 gap-4">
        <div className="w-full px-4 pb-4">
          <PromptInput globalDrop multiple onSubmit={handleSubmit}>
            <PromptInputBody>
              <PromptInputAttachments>
                {(attachment: any) => (
                  <PromptInputAttachment data={attachment} />
                )}
              </PromptInputAttachments>
              <PromptInputTextarea
                onChange={(event: any) => setText(event.target.value)}
                ref={textareaRef}
                value={text}
                className="text-base leading-[1.65rem]"
              />
            </PromptInputBody>
            <PromptInputFooter>
              <PromptInputTools>
                <PromptInputActionMenu>
                  <PromptInputActionMenuTrigger />
                  <PromptInputActionMenuContent>
                    <PromptInputActionAddAttachments />
                  </PromptInputActionMenuContent>
                </PromptInputActionMenu>
                <PromptInputSpeechButton
                  onTranscriptionChange={setText}
                  textareaRef={textareaRef}
                />
                <PromptInputButton
                  onClick={() => setUseWebSearch(!useWebSearch)}
                  variant={useWebSearch ? "default" : "ghost"}
                >
                  <GlobeIcon size={16} />
                  <span>Search</span>
                </PromptInputButton>
              </PromptInputTools>
              <PromptInputTools>
                <PromptInputModelSelect onValueChange={setModel} value={model}>
                  <PromptInputModelSelectTrigger>
                    <PromptInputModelSelectValue />
                  </PromptInputModelSelectTrigger>
                  <PromptInputModelSelectContent>
                    {models.map((model: any) => (
                      <PromptInputModelSelectItem
                        key={model.id || model.name}
                        value={model.id || model.name}
                      >
                        {model.name || model.id}
                      </PromptInputModelSelectItem>
                    ))}
                  </PromptInputModelSelectContent>
                </PromptInputModelSelect>
                <PromptInputSubmit
                  disabled={(!text.trim() && !status) || status === "streaming"}
                  status={status}
                />
              </PromptInputTools>
            </PromptInputFooter>
          </PromptInput>
        </div>
      </div>
    </ChatContainer>
  )
}

export default Example

"use client"

import { useEffect, useRef } from "react"

type MapboxStyle =
  | "streets-v12"
  | "outdoors-v12"
  | "light-v11"
  | "dark-v11"
  | "satellite-v9"
  | "satellite-streets-v12"
  | "navigation-day-v1"
  | "navigation-night-v1"

interface MapboxPointerProps {
  latitude: number
  longitude: number
  zoom?: number
  mapboxToken?: string
  googleMapsUrl?: string
  markerColor?: string
  className?: string
  interactive?: boolean
  style?: MapboxStyle
  label?: string
  labelHref?: string
  clickForDirections?: boolean
}

declare global {
  interface Window {
    mapboxgl: any
  }
}

export function MapboxPointer({
  latitude,
  longitude,
  zoom = 13,
  mapboxToken,
  googleMapsUrl,
  markerColor = "#679BFF",
  className = "",
  interactive = true,
  style = "streets-v12",
  label,
  labelHref,
  clickForDirections = false,
}: MapboxPointerProps) {
  const mapContainer = useRef<HTMLDivElement>(null)
  const map = useRef<any>(null)

  // Get token from props or environment
  const token = mapboxToken || process.env.NEXT_PUBLIC_MAPBOX_TOKEN || ""

  useEffect(() => {
    // Check if token is available
    if (!token) {
      console.warn(
        "MapboxPointer: No Mapbox token found. Please provide mapboxToken prop or set NEXT_PUBLIC_MAPBOX_TOKEN environment variable."
      )
      return
    }

    // Reset map if it exists
    if (map.current) {
      map.current.remove()
      map.current = null
    }

    const loadMapbox = () => {
      // Load CSS if not already loaded
      if (!document.querySelector('link[href*="mapbox-gl.css"]')) {
        const link = document.createElement("link")
        link.href = "https://api.mapbox.com/mapbox-gl-js/v3.0.1/mapbox-gl.css"
        link.rel = "stylesheet"
        document.head.appendChild(link)
      }

      // Load JS if not already loaded
      if (!document.querySelector('script[src*="mapbox-gl.js"]')) {
        const script = document.createElement("script")
        script.src = "https://api.mapbox.com/mapbox-gl-js/v3.0.1/mapbox-gl.js"
        script.async = true
        script.onload = initializeMap
        script.onerror = () => {
          console.error("[v0] Failed to load Mapbox GL JS")
        }
        document.head.appendChild(script)
      } else {
        // Script already loaded, just initialize
        initializeMap()
      }
    }

    const initializeMap = () => {
      if (!mapContainer.current || !window.mapboxgl) return

      try {
        window.mapboxgl.accessToken = token

        map.current = new window.mapboxgl.Map({
          container: mapContainer.current,
          style: `mapbox://styles/mapbox/${style}`,
          center: [longitude, latitude],
          zoom: zoom,
          interactive: interactive,
          attributionControl: false,
        })

        map.current.on("load", () => {
          setTimeout(() => {
            const logoElements = document.querySelectorAll(
              ".mapboxgl-ctrl-logo, .mapboxgl-ctrl-bottom-left, .mapboxgl-ctrl-bottom-right"
            )
            logoElements.forEach((el: Element) => {
              if (el instanceof HTMLElement) {
                el.style.display = "none"
                el.style.visibility = "hidden"
                el.style.opacity = "0"
              }
            })
            // Also hide parent containers
            const ctrlContainers = document.querySelectorAll(".mapboxgl-ctrl")
            ctrlContainers.forEach((el: Element) => {
              if (
                el instanceof HTMLElement &&
                el.querySelector(".mapboxgl-ctrl-logo")
              ) {
                el.style.display = "none"
              }
            })
          }, 100)

          const el = document.createElement("div")
          el.className = "custom-marker"
          el.style.width = "24px"
          el.style.height = "24px"
          el.innerHTML = `
            <div style="position: relative; height: 100%; width: 100%;">
              <div class="animate-marker" style="position: absolute; left: 50%; top: 50%; width: 16px; height: 16px; border-radius: 50%; background-color: ${markerColor};"></div>
              <div style="position: absolute; left: 50%; top: 50%; transform: translate(-50%, -50%); width: 24px; height: 24px; border-radius: 50%; border: 3px solid white; background-color: ${markerColor}; box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);"></div>
            </div>
          `

          new window.mapboxgl.Marker(el)
            .setLngLat([longitude, latitude])
            .addTo(map.current)
        })
      } catch (error) {
        console.error("[v0] Error initializing map:", error)
      }
    }

    if (window.mapboxgl) {
      initializeMap()
    } else {
      loadMapbox()
    }

    return () => {
      if (map.current) {
        map.current.remove()
        map.current = null
      }
    }
  }, [latitude, longitude, zoom, token, markerColor, interactive, style])

  const handleClick = () => {
    if (clickForDirections) {
      const directionsUrl = `https://www.google.com/maps/dir/?api=1&destination=${latitude},${longitude}`
      window.open(directionsUrl, "_blank", "noopener,noreferrer")
    } else if (googleMapsUrl) {
      window.open(googleMapsUrl, "_blank", "noopener,noreferrer")
    }
  }

  // Show error message if no token
  if (!token) {
    return (
      <div className="flex h-96 items-center justify-center rounded-lg border">
        <p className="text-muted-foreground">Mapbox token not configured</p>
      </div>
    )
  }

  return (
    <>
      <div
        className={`bg-card relative overflow-hidden rounded-lg border ${
          clickForDirections || googleMapsUrl ? "cursor-pointer" : ""
        } ${className}`}
        onClick={handleClick}
      >
        <div ref={mapContainer} className="h-full w-full" />
        {label &&
          (labelHref ? (
            <a
              href={labelHref}
              target="_blank"
              rel="noopener noreferrer"
              className="bg-background border-border text-foreground absolute right-4 bottom-4 z-10 flex cursor-pointer items-center gap-1 rounded border px-3 py-1 text-xs font-normal"
              onClick={(e) => e.stopPropagation()}
            >
              {label}
            </a>
          ) : (
            <div className="bg-background border-border text-foreground absolute right-4 bottom-4 z-10 flex items-center gap-1 rounded border px-3 py-1 text-xs font-normal">
              {label}
            </div>
          ))}
      </div>
      <style jsx>{`
        @keyframes marker {
          0% {
            transform: translate(-50%, -50%) scale(1);
            opacity: 1;
          }
          100% {
            transform: translate(-50%, -50%) scale(6);
            opacity: 0;
          }
        }

        :global(.animate-marker) {
          animation: marker 4s ease-out infinite;
        }

        :global(.mapboxgl-ctrl-logo),
        :global(.mapboxgl-ctrl-attrib),
        :global(.mapboxgl-ctrl-bottom-left),
        :global(.mapboxgl-ctrl-bottom-right),
        :global(.mapboxgl-compact) {
          display: none !important;
          visibility: hidden !important;
          opacity: 0 !important;
        }
      `}</style>
    </>
  )
}

