"use client"

import * as React from "react"

import { cn } from "@/lib/utils"

export interface MarqueeProps extends React.HTMLAttributes<HTMLDivElement> {
  /**
   * Animation speed in seconds for one complete loop
   * @default 30
   */
  speed?: number
  /**
   * Direction of the carousel animation
   * @default "left"
   */
  direction?: "left" | "right"
  /**
   * Whether to pause animation on hover
   * @default true
   */
  pauseOnHover?: boolean
  /**
   * Gap between items in pixels
   * @default 70
   */
  gap?: number
  /**
   * Whether to show fade effect on edges
   * @default true
   */
  showFade?: boolean
  /**
   * Fade intensity (0-100)
   * @default 12.5
   */
  fadeIntensity?: number
  /**
   * Number of times to duplicate the children for seamless loop
   * @default 2
   */
  duplicates?: number
  /**
   * Custom class for each item container
   */
  itemClassName?: string
  /**
   * Children to display in the marquee
   */
  children: React.ReactNode
}

export function Marquee({
  children,
  speed = 30,
  direction = "left",
  pauseOnHover = true,
  gap = 70,
  showFade = true,
  fadeIntensity = 12.5,
  duplicates = 2,
  itemClassName,
  className,
  ...props
}: MarqueeProps) {
  const [isPaused, setIsPaused] = React.useState(false)
  const containerRef = React.useRef<HTMLDivElement>(null)

  // Create duplicated children array for seamless loop
  const childrenArray = React.Children.toArray(children)
  const duplicatedChildren = React.useMemo(() => {
    return Array.from({ length: duplicates }, () => childrenArray).flat()
  }, [childrenArray, duplicates])

  // Calculate animation duration based on number of items
  const animationDuration = `${speed}s`

  const maskImage = showFade
    ? `linear-gradient(to right, rgba(0, 0, 0, 0) 0%, rgb(0, 0, 0) ${fadeIntensity}%, rgb(0, 0, 0) ${100 - fadeIntensity}%, rgba(0, 0, 0, 0) 100%)`
    : undefined

  return (
    <div
      ref={containerRef}
      className={cn("relative w-full overflow-hidden", className)}
      style={{
        maskImage,
        WebkitMaskImage: maskImage,
      }}
      onMouseEnter={() => pauseOnHover && setIsPaused(true)}
      onMouseLeave={() => pauseOnHover && setIsPaused(false)}
      {...props}
    >
      <div
        className="flex w-max"
        style={{
          gap: `${gap}px`,
          animation: `scroll-${direction} ${animationDuration} linear infinite`,
          animationPlayState: isPaused ? "paused" : "running",
        }}
      >
        {duplicatedChildren.map((child, index) => (
          <div
            key={index}
            className={cn(
              "flex flex-shrink-0 items-center justify-center",
              itemClassName
            )}
          >
            {child}
          </div>
        ))}
      </div>

      <style jsx>{`
        @keyframes scroll-left {
          0% {
            transform: translateX(0);
          }
          100% {
            transform: translateX(-50%);
          }
        }

        @keyframes scroll-right {
          0% {
            transform: translateX(-50%);
          }
          100% {
            transform: translateX(0);
          }
        }
      `}</style>
    </div>
  )
}

"use client"

import * as React from "react"
import { cn } from "@/lib/utils"

/* ------------------------------------------------------------------
 * Context for sharing state between compound components
 * ------------------------------------------------------------------ */

type ProductCardVariant = "default" | "inner"
type ProductCardSize = "default" | "sm" | "small" | "lg" | "large"

interface ProductCardContextValue {
  variant: ProductCardVariant
  size: ProductCardSize
  animated: boolean
}

const ProductCardContext = React.createContext<ProductCardContextValue | null>(null)

function useProductCardContext() {
  const context = React.useContext(ProductCardContext)
  if (!context) {
    throw new Error("ProductCard compound components must be used within <ProductCard>")
  }
  return context
}

function normalizeSize(size: ProductCardSize): "default" | "sm" | "lg" {
  if (size === "small") return "sm"
  if (size === "large") return "lg"
  return size as "default" | "sm" | "lg"
}

/* ------------------------------------------------------------------
 * ProductCard (root)
 * ------------------------------------------------------------------ */

interface ProductCardProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Card variant - "default" shows content below image, "inner" shows content inside */
  variant?: ProductCardVariant
  /** Card size - "sm" | "small", "default", or "lg" | "large" */
  size?: ProductCardSize
  /** Enable hover/active animation on image - defaults to true */
  animated?: boolean
  /** Callback when the card is clicked */
  onCardClick?: () => void
}

const ProductCard = React.forwardRef<HTMLDivElement, ProductCardProps>(
  ({ className, variant = "default", size = "default", animated = true, onCardClick, children, ...props }, ref) => {
    return (
      <ProductCardContext.Provider value={{ variant, size, animated }}>
        <div
          ref={ref}
          className={cn("cursor-pointer overflow-hidden rounded-lg", className)}
          onClick={onCardClick}
          {...props}
        >
          {children}
        </div>
      </ProductCardContext.Provider>
    )
  },
)
ProductCard.displayName = "ProductCard"

/* ------------------------------------------------------------------
 * ProductCardImage
 * ------------------------------------------------------------------ */

interface ProductCardImageProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Image source URL */
  src: string
  /** Image alt text for accessibility */
  alt: string
  /** Custom class for the image element itself */
  imageClassName?: string
}

const ProductCardImage = React.forwardRef<HTMLDivElement, ProductCardImageProps>(
  ({ className, src, alt, imageClassName, children, ...props }, ref) => {
    const { size, animated } = useProductCardContext()
    const normalizedSize = normalizeSize(size)

    return (
      <div
        ref={ref}
        className={cn(
          "relative aspect-square w-full overflow-hidden rounded-lg transition-colors duration-150",
          "bg-muted",
          "[&:hover]:bg-muted/80 [&:active]:bg-muted/80",
          animated && "[&:hover>img]:-translate-y-1 [&:active>img]:-translate-y-1",
          className,
        )}
        {...props}
      >
        <img
          src={src || "/placeholder.svg"}
          alt={alt}
          className={cn(
            "absolute inset-0 h-full w-full object-contain",
            animated && "transition-transform duration-200 ease-in-out",
            normalizedSize === "sm" && "p-4",
            normalizedSize === "default" && "p-8",
            normalizedSize === "lg" && "p-12",
            imageClassName,
          )}
        />
        {children}
      </div>
    )
  },
)
ProductCardImage.displayName = "ProductCardImage"

/* ------------------------------------------------------------------
 * ProductCardBadge
 * ------------------------------------------------------------------ */

interface ProductCardBadgeProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  /** Whether the badge is in active/selected state */
  isActive?: boolean
  /** Icon to display before label */
  icon?: React.ReactNode
}

const ProductCardBadge = React.forwardRef<HTMLButtonElement, ProductCardBadgeProps>(
  ({ className, isActive = false, icon, children, onClick, ...props }, ref) => {
    const { size } = useProductCardContext()
    const normalizedSize = normalizeSize(size)

    return (
      <button
        ref={ref}
        type="button"
        onClick={(e) => {
          e.stopPropagation()
          onClick?.(e)
        }}
        className={cn(
          "absolute flex items-center gap-1 rounded font-medium transition-colors",
          normalizedSize === "sm" && "right-1.5 top-1.5 px-1.5 py-0.5 text-[10px]",
          normalizedSize === "default" && "right-2 top-2 px-2 py-1 text-xs",
          normalizedSize === "lg" && "right-3 top-3 px-3 py-1.5 text-sm",
          isActive
            ? "bg-primary text-primary-foreground hover:bg-primary/90"
            : "bg-popover text-popover-foreground hover:bg-accent",
          "focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2",
          className,
        )}
        {...props}
      >
        {icon && <span aria-hidden="true">{icon}</span>}
        {children}
      </button>
    )
  },
)
ProductCardBadge.displayName = "ProductCardBadge"

/* ------------------------------------------------------------------
 * ProductCardContent (container for title, subtitle, metric)
 * ------------------------------------------------------------------ */

interface ProductCardContentProps extends React.HTMLAttributes<HTMLDivElement> {}

const ProductCardContent = React.forwardRef<HTMLDivElement, ProductCardContentProps>(
  ({ className, children, ...props }, ref) => {
    const { variant, size } = useProductCardContext()
    const normalizedSize = normalizeSize(size)

    return (
      <div
        ref={ref}
        className={cn(
          "flex items-start justify-between gap-2",
          variant === "default" && normalizedSize === "sm" && "px-0.5 py-2",
          variant === "default" && normalizedSize === "default" && "px-1 py-3",
          variant === "default" && normalizedSize === "lg" && "px-2 py-4",
          variant === "inner" && normalizedSize === "sm" && "absolute inset-x-0 bottom-0 items-end p-2",
          variant === "inner" && normalizedSize === "default" && "absolute inset-x-0 bottom-0 items-end p-3",
          variant === "inner" && normalizedSize === "lg" && "absolute inset-x-0 bottom-0 items-end p-4",
          className,
        )}
        {...props}
      >
        {children}
      </div>
    )
  },
)
ProductCardContent.displayName = "ProductCardContent"

