"use client"

import * as React from "react"
import { useEffect, useState } from "react"
import { TerminalIcon } from "lucide-react"
import { Highlight, PrismTheme } from "prism-react-renderer"

import { cn } from "@/lib/utils"
import { CopyButton } from "@/registry/delta-ui/delta/copy-button"
import {
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
} from "@/components/ui/tabs"

type PackageManager = "npm" | "yarn" | "pnpm" | "bun"

// Inline theme definitions
const defaultTheme: PrismTheme = {
  plain: {
    color: "#FFFFFF",
    backgroundColor: "#161616",
  },
  styles: [
    {
      types: ["comment"],
      style: {
        color: "#757575",
        fontStyle: "italic",
      },
    },
    {
      types: ["keyword", "property", "property-access", "attr-name"],
      style: {
        color: "#77b7d7",
      },
    },
    {
      types: ["tag"],
      style: {
        color: "#dfab5c",
      },
    },
    {
      types: ["punctuation", "symbol", "dom"],
      style: {
        color: "#ffffff",
      },
    },
    {
      types: ["definition", "function"],
      style: {
        color: "#86d9ca",
      },
    },
    {
      types: ["string", "char", "attr-value"],
      style: {
        color: "#977cdc",
      },
    },
    {
      types: ["static", "number"],
      style: {
        color: "#ff6658",
      },
    },
  ],
}

const lightTheme: PrismTheme = {
  plain: {
    color: "#24292e",
    backgroundColor: "#F9F9F9",
  },
  styles: [
    {
      types: ["comment"],
      style: {
        color: "#8b949e",
        fontStyle: "italic",
      },
    },
    {
      types: ["variable", "parameter"],
      style: {
        color: "#e36209",
      },
    },
    {
      types: ["keyword", "builtin", "function-definition"],
      style: {
        color: "#d73a49",
      },
    },
    {
      types: ["property", "property-access", "attr-name"],
      style: {
        color: "#005cc5",
      },
    },
    {
      types: ["tag"],
      style: {
        color: "#22863a",
      },
    },
    {
      types: ["punctuation", "symbol", "dom", "operator"],
      style: {
        color: "#24292e",
      },
    },
    {
      types: ["function"],
      style: {
        color: "#6f42c1",
      },
    },
    {
      types: ["class-name"],
      style: {
        color: "#6f42c1",
      },
    },
    {
      types: ["string", "char", "attr-value"],
      style: {
        color: "#032f62",
      },
    },
    {
      types: ["static", "number"],
      style: {
        color: "#005cc5",
      },
    },
  ],
}

interface CodeBlockProps {
  // Package manager commands
  npm?: string
  yarn?: string
  pnpm?: string
  bun?: string
  defaultPackageManager?: PackageManager

  // Code highlighting props
  code?: string
  language?: string
  filename?: string
  showLineNumbers?: boolean
  theme?: PrismTheme
  adaptiveTheme?: {
    light: PrismTheme
    dark: PrismTheme
  }

  className?: string
}

export function CodeBlock({
  npm,
  yarn,
  pnpm,
  bun,
  defaultPackageManager = "npm",
  code,
  language = "typescript",
  filename,
  showLineNumbers = true,
  theme,
  adaptiveTheme,
  className,
}: CodeBlockProps) {
  const [packageManager, setPackageManager] = React.useState<PackageManager>(
    defaultPackageManager
  )
  const [isDark, setIsDark] = useState(false)

  // Hook to detect dark mode
  useEffect(() => {
    const checkDarkMode = () => {
      setIsDark(document.documentElement.classList.contains("dark"))
    }

    checkDarkMode()

    const observer = new MutationObserver((mutations) => {
      mutations.forEach((mutation) => {
        if (
          mutation.type === "attributes" &&
          mutation.attributeName === "class"
        ) {
          checkDarkMode()
        }
      })
    })

    observer.observe(document.documentElement, {
      attributes: true,
      attributeFilter: ["class"],
    })

    return () => observer.disconnect()
  }, [])

  const commands = React.useMemo(() => {
    return {
      npm,
      yarn,
      pnpm,
      bun,
    }
  }, [npm, yarn, pnpm, bun])

  const availableCommands = Object.entries(commands).filter(
    ([, command]) => command
  )

  // Select theme based on adaptiveTheme prop or auto-detect light/dark mode
  const selectedTheme = adaptiveTheme
    ? isDark
      ? adaptiveTheme.dark
      : adaptiveTheme.light
    : theme || (isDark ? defaultTheme : lightTheme)

  // Use the theme's text foreground color for the copy button
  const iconColor = selectedTheme.plain?.color || "#000000"

  // If we have package manager commands, show the tabbed interface
  if (availableCommands.length > 0) {
    return (
      <div
        className={cn(
          "bg-card text-card-foreground overflow-x-auto rounded-lg border",
          className
        )}
        data-slot="tabs"
      >
        <Tabs
          value={packageManager}
          className="gap-0"
          onValueChange={(value) => setPackageManager(value as PackageManager)}
        >
          <div className="bg-secondary flex items-center gap-2 border-b px-3 py-1">
            <div className="bg-foreground flex size-4 items-center justify-center rounded-[1px] opacity-70">
              <TerminalIcon className="text-code size-3" />
            </div>
            <TabsList className="rounded-none bg-transparent p-0">
              {availableCommands.map(([key]) => (
                <TabsTrigger
                  key={key}
                  value={key}
                  className="data-[state=active]:border-primary h-7 rounded-none border-0 pt-0.5 data-[state=active]:border-b-2 data-[state=active]:bg-transparent data-[state=active]:shadow-none"
                >
                  {key}
                </TabsTrigger>
              ))}
            </TabsList>
          </div>
          <div className="no-scrollbar overflow-x-auto">
            {availableCommands.map(([key, command]) => (
              <TabsContent key={key} value={key} className="mt-0">
                <div
                  className="relative py-4"
                  style={{
                    backgroundColor: selectedTheme.plain?.backgroundColor,
                  }}
                >
                  <Highlight
                    theme={selectedTheme}
                    code={command || ""}
                    language="bash"
                  >
                    {({
                      className: highlightClassName,
                      style,
                      tokens,
                      getLineProps,
                      getTokenProps,
                    }) => (
                      <pre
                        className={`${highlightClassName} w-full overflow-x-auto px-4 font-mono text-sm leading-relaxed font-medium`}
                        style={{
                          ...style,
                          fontSize: className?.includes("text-lg")
                            ? "1.125rem"
                            : className?.includes("text-base")
                              ? "1rem"
                              : undefined,
                        }}
                      >
                        {tokens.map((line, i) => (
                          <div key={i} {...getLineProps({ line })}>
                            {line.map((token, key) => (
                              <span key={key} {...getTokenProps({ token })} />
                            ))}
                          </div>
                        ))}
                      </pre>
                    )}
                  </Highlight>
                </div>
              </TabsContent>
            ))}
          </div>
        </Tabs>
        <CopyButton
          value={commands[packageManager] || ""}
          className="absolute top-2 right-2 z-10 size-7 opacity-70 hover:opacity-100 focus-visible:opacity-100"
          iconColor={iconColor}
        />
      </div>
    )
  }

  // If we have code to highlight, show the syntax highlighted version
  if (code) {
    return (
      <div
        className={cn(
          "border-border pointer-events-auto w-full max-w-full overflow-x-auto overflow-y-hidden rounded-sm border",
          className
        )}
      >
        {filename && (
          <div className="bg-secondary flex items-center justify-between border-b">
            <div className="flex items-center gap-2 px-3 py-1">
              <div className="bg-foreground flex size-4 items-center justify-center rounded-[1px] opacity-70">
                <TerminalIcon className="text-code size-3" />
              </div>
              <span className="text-sm font-medium">{filename}</span>
            </div>
            <CopyButton value={code} className="mr-3" iconColor={iconColor} />
          </div>
        )}

        <div
          className="relative max-h-[calc(530px-44px)] w-full py-4"
          style={{
            backgroundColor: selectedTheme.plain?.backgroundColor,
          }}
        >
          {!filename && (
            <div className="absolute top-4 right-3">
              <CopyButton value={code} iconColor={iconColor} />
            </div>
          )}

          <Highlight
            theme={selectedTheme}
            code={code.trim()}
            language={language}
          >
            {({
              className: highlightClassName,
              style,
              tokens,
              getLineProps,
              getTokenProps,
            }) => (
              <pre
                className={`${highlightClassName} thin-scrollbar max-h-[calc(530px-88px)] w-full overflow-x-auto overflow-y-auto font-mono text-sm leading-relaxed font-medium`}
                style={{
                  ...style,
                  fontSize: className?.includes("text-lg")
                    ? "1.125rem"
                    : className?.includes("text-base")
                      ? "1rem"
                      : undefined,
                  scrollbarWidth: "thin",
                  scrollbarColor:
                    selectedTheme.plain?.backgroundColor?.toLowerCase() ===
                      "#ffffff" ||
                    selectedTheme.plain?.backgroundColor === "#FAFAFA"
                      ? "#d1d5db transparent"
                      : "#4b5563 transparent",
                }}
              >
                {tokens.map((line, i) => (
                  <div
                    key={i}
                    {...getLineProps({ line })}
                    className="flex items-center px-4 py-px"
                  >
                    {showLineNumbers && (
                      <span
                        className="mr-4 flex items-center text-right text-sm select-none"
                        style={{
                          color:
                            selectedTheme.plain?.backgroundColor ===
                              "#FFFFFF" ||
                            selectedTheme.plain?.backgroundColor === "#FAFAFA"
                              ? "#999999"
                              : "#757575",
                          minWidth: "1.5rem",
                          fontSize: className?.includes("text-lg")
                            ? "1.125rem"
                            : className?.includes("text-base")
                              ? "1rem"
                              : undefined,
                        }}
                      >
                        {i + 1}
                      </span>
                    )}
                    <span className={!showLineNumbers ? "ml-0" : ""}>
                      {line.map((token, key) => (
                        <span key={key} {...getTokenProps({ token })} />
                      ))}
                    </span>
                  </div>
                ))}
              </pre>
            )}
          </Highlight>
        </div>
      </div>
    )
  }

  // Return null if no content
  return null
}

"use client"

import * as React from "react"
import { CheckIcon, ClipboardIcon } from "lucide-react"

import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from "@/components/ui/tooltip"

export function copyToClipboard(value: string) {
  navigator.clipboard.writeText(value)
}

interface CopyButtonProps extends React.ComponentProps<typeof Button> {
  value: string
  variant?:
    | "ghost"
    | "outline"
    | "secondary"
    | "default"
    | "destructive"
    | "link"
  iconColor?: string
}

export function CopyButton({
  value,
  className,
  variant = "ghost",
  iconColor,
  ...props
}: CopyButtonProps) {
  const [hasCopied, setHasCopied] = React.useState(false)

  React.useEffect(() => {
    if (hasCopied) {
      const timer = setTimeout(() => {
        setHasCopied(false)
      }, 2000)
      return () => clearTimeout(timer)
    }
  }, [hasCopied])

  return (
    <Tooltip>
      <TooltipTrigger asChild>
        <Button
          data-slot="copy-button"
          size="icon"
          variant={variant}
          className={cn(
            "size-7 hover:opacity-100 focus-visible:opacity-100",
            className
          )}
          onClick={() => {
            copyToClipboard(value)
            setHasCopied(true)
          }}
          {...props}
        >
          <span className="sr-only">Copy</span>
          {hasCopied ? (
            <CheckIcon style={iconColor ? { color: iconColor } : {}} />
          ) : (
            <ClipboardIcon style={iconColor ? { color: iconColor } : {}} />
          )}
        </Button>
      </TooltipTrigger>
      <TooltipContent>
        {hasCopied ? "Copied" : "Copy to Clipboard"}
      </TooltipContent>
    </Tooltip>
  )
}

"use client"

import { useEffect, useRef, useState } from "react"
import { Cambio } from "cambio"

import { cn } from "@/lib/utils"

type MotionPreset = "snappy" | "smooth" | "bouncy" | "reduced"

interface CambioImageProps {
  src: string
  alt: string
  width: number
  height: number
  loading?: "lazy" | "eager"
  index?: number
  motion?:
    | MotionPreset
    | {
        trigger?: MotionPreset
        popup?: MotionPreset
        backdrop?: MotionPreset
      }
  dismissible?: boolean
  className?: string
  draggable?: boolean
  enableInitialAnimation?: boolean
  dismissOnImageClick?: boolean
}

export function CambioImage({
  src,
  alt,
  width,
  height,
  loading = "lazy",
  index = 0,
  motion = "snappy",
  dismissible = false,
  className,
  draggable = false,
  enableInitialAnimation = true,
  dismissOnImageClick = false,
}: CambioImageProps) {
  const [isVisible, setIsVisible] = useState(false)

  /* NEW â€” track the zoom state */
  const [open, setOpen] = useState(false)
  const ref = useRef<HTMLDivElement>(null)

  useEffect(() => {
    if (!enableInitialAnimation) {
      setIsVisible(true)
      return
    }

    const io = new IntersectionObserver(
      ([e]) => {
        if (e.isIntersecting) {
          setIsVisible(true)
          io.disconnect()
        }
      },
      { threshold: 0.1 }
    )
    if (ref.current) {
      io.observe(ref.current)
    }
    return () => io.disconnect()
  }, [enableInitialAnimation])

  /* Lower z-index when open to stay behind the popup */
  const zIndex = open ? 50 : 10 + index

  return (
    <span
      ref={ref}
      className={cn(
        "relative inline-block w-full transition-all duration-500 ease-out",
        className
      )}
      style={{
        opacity: enableInitialAnimation ? (isVisible ? 1 : 0) : 1,
        filter: enableInitialAnimation
          ? isVisible
            ? "blur(0)"
            : "blur(4px)"
          : "blur(0)",
        zIndex,
      }}
    >
      {/* @ts-ignore */}
      <Cambio.Root
        motion={motion}
        dismissible={dismissible}
        open={open}
        onOpenChange={setOpen}
      >
        {/* @ts-ignore */}
        <Cambio.Trigger
          className={cn(
            "relative w-full overflow-hidden",
            !open && "cursor-zoom-in"
          )}
          style={{ pointerEvents: open ? "none" : "auto" }}
        >
          <img
            src={src}
            alt={alt}
            width={width}
            height={height}
            loading={loading}
            draggable={draggable}
            className={cn("h-auto w-full", className)}
            style={{ pointerEvents: "none" }}
          />
        </Cambio.Trigger>

        {/* @ts-ignore */}
        <Cambio.Portal>
          {/* @ts-ignore */}
          <Cambio.Backdrop
            motion="reduced"
            className="fixed inset-0 z-[100] bg-black/40"
          />
          {/* @ts-ignore */}
          <Cambio.Popup className="z-[101] w-full overflow-hidden md:w-[70%]">
            <img
              src={src}
              alt={alt}
              width={width}
              height={height}
              loading="eager"
              draggable={draggable}
              className={cn(
                "h-auto w-full object-contain",
                dismissOnImageClick && "cursor-zoom-out"
              )}
              style={{ pointerEvents: dismissOnImageClick ? "auto" : "none" }}
              onClick={dismissOnImageClick ? () => setOpen(false) : undefined}
            />
          </Cambio.Popup>
        </Cambio.Portal>
      </Cambio.Root>
    </span>
  )
}

"use client"

import React from "react"
import { motion } from "framer-motion"
import { ChevronLeftIcon, ChevronRightIcon } from "lucide-react"
import { Autoplay, EffectCards, Navigation, Pagination } from "swiper/modules"
import { Swiper, SwiperSlide } from "swiper/react"

import "swiper/css/effect-cards"
import "swiper/css/pagination"
import "swiper/css/navigation"
import "swiper/css"

import { cn } from "@/lib/utils"

interface CardDeckContainerProps {
  children: React.ReactNode
  className?: string
  showPagination?: boolean
  showNavigation?: boolean
  infinite?: boolean
  autoplay?: boolean
  spaceBetween?: number
  enableInitialAnimation?: boolean
}

interface CardDeckItemProps {
  children: React.ReactNode
  className?: string
}

// New modular API components
export function CardDeckContainer({
  children,
  className,
  showPagination = false,
  showNavigation = false,
  infinite = true,
  autoplay = false,
  spaceBetween = 40,
  enableInitialAnimation = true,
}: CardDeckContainerProps) {
  const css = `
  .card-deck {
    padding: 20px 0 50px 0 !important;
  }
  .card-deck .swiper-slide {
    overflow: visible !important;
  }
  .card-deck .swiper-slide-shadow-cards {
    border-radius: 1.5rem !important;
  }
  `

  return (
    <motion.div
      initial={enableInitialAnimation ? { opacity: 0, translateY: 20 } : false}
      animate={enableInitialAnimation ? { opacity: 1, translateY: 0 } : false}
      transition={
        enableInitialAnimation
          ? {
              duration: 0.3,
              delay: 0.5,
            }
          : undefined
      }
      className={cn("relative w-full max-w-3xl overflow-visible", className)}
    >
      <style>{css}</style>

      <Swiper
        spaceBetween={spaceBetween}
        autoplay={
          autoplay
            ? {
                delay: 1000,
                disableOnInteraction: false,
              }
            : false
        }
        effect="cards"
        grabCursor={true}
        loop={infinite}
        allowTouchMove={true}
        touchRatio={1}
        resistance={true}
        resistanceRatio={infinite ? 0 : 0.85}
        pagination={
          showPagination
            ? {
                clickable: true,
              }
            : false
        }
        navigation={
          showNavigation
            ? {
                nextEl: ".swiper-button-next",
                prevEl: ".swiper-button-prev",
              }
            : false
        }
        className="card-deck h-[360px] w-[240px] sm:h-[420px] sm:w-[280px]"
        modules={[EffectCards, Autoplay, Pagination, Navigation]}
      >
        {children &&
          React.Children.map(children, (child, index) => {
            if (React.isValidElement(child)) {
              const slideClass = child.props.className || "rounded-3xl"

              return (
                <SwiperSlide
                  key={index}
                  className={cn(slideClass, "overflow-hidden")}
                >
                  {child.props.children}
                </SwiperSlide>
              )
            }
            return null
          })}
        {showNavigation && (
          <div>
            <div className="swiper-button-next after:hidden">
              <ChevronRightIcon className="h-6 w-6 text-white" />
            </div>
            <div className="swiper-button-prev after:hidden">
              <ChevronLeftIcon className="h-6 w-6 text-white" />
            </div>
          </div>
        )}
      </Swiper>
    </motion.div>
  )
}

export function CardDeckItem({ children, className }: CardDeckItemProps) {
  // This component passes its children and className to CardDeckContainer for rendering
  return <>{children}</>
}

// Legacy API for backward compatibility
interface CardDeckProps {
  images: { src: string; alt: string }[]
  infinite?: boolean
  autoplay?: boolean
  showPagination?: boolean
  showNavigation?: boolean
  spaceBetween?: number
  className?: string
  enableInitialAnimation?: boolean
}

export function CardDeck({
  images,
  infinite = true,
  autoplay = false,
  showPagination = false,
  showNavigation = false,
  spaceBetween = 40,
  className,
  enableInitialAnimation = true,
}: CardDeckProps) {
  return (
    <CardDeckContainer
      infinite={infinite}
      autoplay={autoplay}
      showPagination={showPagination}
      showNavigation={showNavigation}
      spaceBetween={spaceBetween}
      className={className}
      enableInitialAnimation={enableInitialAnimation}
    >
      {images &&
        images.map((image, index) => (
          <CardDeckItem key={index} className="rounded-3xl">
            <img
              className="h-full w-full rounded-3xl object-cover"
              src={image.src}
              alt={image.alt}
            />
          </CardDeckItem>
        ))}
    </CardDeckContainer>
  )
}

import React from "react"
import {
  AlertCircle,
  AlertTriangle,
  CheckCircle,
  Info,
  Lightbulb,
  XCircle,
} from "lucide-react"

const admonitionConfig = {
  note: {
    bgColor: "bg-blue-50 dark:bg-blue-950/30",
    borderColor: "border-blue-200 dark:border-blue-800",
    textColor: "text-blue-700 dark:text-blue-300",
    iconColor: "text-blue-600 dark:text-blue-400",
    icon: Info,
  },
  tip: {
    bgColor: "bg-green-50 dark:bg-green-950/30",
    borderColor: "border-green-200 dark:border-green-800",
    textColor: "text-green-700 dark:text-green-300",
    iconColor: "text-green-600 dark:text-green-400",
    icon: Lightbulb,
  },
  info: {
    bgColor: "bg-cyan-50 dark:bg-cyan-950/30",
    borderColor: "border-cyan-200 dark:border-cyan-800",
    textColor: "text-cyan-700 dark:text-cyan-300",
    iconColor: "text-cyan-600 dark:text-cyan-400",
    icon: Info,
  },
  warning: {
    bgColor: "bg-amber-50 dark:bg-amber-950/30",
    borderColor: "border-amber-200 dark:border-amber-700",
    textColor: "text-amber-700 dark:text-amber-300",
    iconColor: "text-amber-600 dark:text-amber-400",
    icon: AlertTriangle,
  },
  danger: {
    bgColor: "bg-red-50 dark:bg-red-950/30",
    borderColor: "border-red-200 dark:border-red-800",
    textColor: "text-red-700 dark:text-red-300",
    iconColor: "text-red-600 dark:text-red-400",
    icon: XCircle,
  },
  success: {
    bgColor: "bg-emerald-50 dark:bg-emerald-950/30",
    borderColor: "border-emerald-200 dark:border-emerald-800",
    textColor: "text-emerald-700 dark:text-emerald-300",
    iconColor: "text-emerald-600 dark:text-emerald-400",
    icon: CheckCircle,
  },
  caution: {
    bgColor: "bg-orange-50 dark:bg-orange-950/30",
    borderColor: "border-orange-200 dark:border-orange-700",
    textColor: "text-orange-700 dark:text-orange-300",
    iconColor: "text-orange-600 dark:text-orange-400",
    icon: AlertCircle,
  },
}

interface AdmonitionProps {
  type?: keyof typeof admonitionConfig
  title?: string
  children: React.ReactNode
  icon?: React.ComponentType<{ className?: string }>
  className?: string
}

export function Admonition({
  type = "note",
  title,
  children,
  icon: CustomIcon,
  className = "",
}: AdmonitionProps) {
  const config = admonitionConfig[type]
  const IconComponent = CustomIcon || config.icon

  return (
    <div
      className={` ${config.bgColor} ${config.borderColor} rounded-lg border p-4 ${className} `.trim()}
    >
      <div className="flex gap-3">
        <div className={`${config.iconColor} mt-0.5 flex-shrink-0`}>
          <IconComponent className="h-5 w-5" style={{ lineHeight: "1lh" }} />
        </div>
        <div className="min-w-0 flex-1">
          {title && (
            <div className={`${config.textColor} mb-1 text-sm font-semibold`}>
              {title}
            </div>
          )}
          <div className={`${config.textColor} text-base leading-relaxed`}>
            {children}
          </div>
        </div>
      </div>
    </div>
  )
}

"use client"

import { useEffect, useRef, useState, type ReactNode } from "react"

import { cn } from "@/lib/utils"

interface XScrollableProps {
  children: ReactNode
  className?: string
  showScrollbar?: boolean
  scrollbarTrackClassName?: string
  scrollbarThumbClassName?: string
  onScroll?: (
    scrollLeft: number,
    scrollWidth: number,
    clientWidth: number
  ) => void
}

export function XScrollable({
  children,
  className,
  showScrollbar = false,
  scrollbarTrackClassName,
  scrollbarThumbClassName,
  onScroll,
}: XScrollableProps) {
  const scrollContainerRef = useRef<HTMLDivElement>(null)
  const [showLeftShadow, setShowLeftShadow] = useState(false)
  const [showRightShadow, setShowRightShadow] = useState(false)

  useEffect(() => {
    const scrollContainer = scrollContainerRef.current
    if (!scrollContainer) return

    const handleScroll = () => {
      const { scrollLeft, scrollWidth, clientWidth } = scrollContainer
      setShowLeftShadow(scrollLeft > 0)
      setShowRightShadow(scrollLeft < scrollWidth - clientWidth)
      onScroll?.(scrollLeft, scrollWidth, clientWidth)
    }

    handleScroll() // Initial check
    scrollContainer.addEventListener("scroll", handleScroll)

    return () => {
      scrollContainer.removeEventListener("scroll", handleScroll)
    }
  }, [onScroll])

  return (
    <div className="relative">
      <div
        ref={scrollContainerRef}
        className={cn(
          "overflow-x-auto overflow-y-hidden",
          showScrollbar
            ? "scrollbar-thin scrollbar-track-transparent scrollbar-thumb-gray-300 hover:scrollbar-thumb-gray-400"
            : "scrollbar-hide",
          className
        )}
      >
        {children}
      </div>
      {showLeftShadow && (
        <div className="from-background pointer-events-none absolute top-0 bottom-0 left-0 w-8 bg-gradient-to-r to-transparent" />
      )}
      {showRightShadow && (
        <div className="from-background pointer-events-none absolute top-0 right-0 bottom-0 w-8 bg-gradient-to-l to-transparent" />
      )}
      {showScrollbar && (
        <div
          className={cn(
            "mt-1 h-1 rounded-full bg-gray-200",
            scrollbarTrackClassName
          )}
        >
          <div
            className={cn(
              "h-full rounded-full bg-gray-400",
              scrollbarThumbClassName
            )}
            style={{
              width: scrollContainerRef.current
                ? `${(scrollContainerRef.current.clientWidth / scrollContainerRef.current.scrollWidth) * 100}%`
                : "0%",
              transform: scrollContainerRef.current
                ? `translateX(${(scrollContainerRef.current.scrollLeft / scrollContainerRef.current.clientWidth) * 100}%)`
                : "translateX(0%)",
            }}
          />
        </div>
      )}
    </div>
  )
}

"use client"

import React, {
  forwardRef,
  useCallback,
  useEffect,
  useRef,
  useState,
  type ReactNode,
} from "react"

import { cn } from "@/lib/utils"
import { XScrollable } from "@/registry/delta-ui/delta/x-scrollable"

// Base Tabs component (Root)
const Tabs = forwardRef<
  HTMLDivElement,
  {
    defaultValue?: string
    value?: string
    onValueChange?: (value: string) => void
    className?: string
    children?: ReactNode
  }
>(
  (
    { defaultValue, value, onValueChange, className, children, ...props },
    ref
  ) => {
    const [activeValue, setActiveValue] = useState(value || defaultValue || "")

    useEffect(() => {
      if (value !== undefined) {
        setActiveValue(value)
      }
    }, [value])

    const handleValueChange = useCallback(
      (newValue: string) => {
        if (value === undefined) {
          setActiveValue(newValue)
        }
        onValueChange?.(newValue)
      },
      [onValueChange, value]
    )

    return (
      <div ref={ref} className={cn("tabs-container", className)} {...props}>
        {React.Children.map(children, (child) => {
          if (!React.isValidElement(child)) return child

          // Only pass tab-related props to tab components
          const childType = child.type
          const isTabComponent =
            childType === TabsList ||
            childType === TabsTrigger ||
            childType === TabsContent ||
            (typeof childType === "function" &&
              ((childType as any).displayName === "TabsList" ||
                (childType as any).displayName === "TabsTrigger" ||
                (childType as any).displayName === "TabsContent"))

          if (isTabComponent) {
            return React.cloneElement(
              child as React.ReactElement<{
                activeValue?: string
                onValueChange?: (value: string) => void
                className?: string
                children?: ReactNode
                [key: string]: any
              }>,
              {
                activeValue,
                onValueChange: handleValueChange,
              }
            )
          }

          return child
        })}
      </div>
    )
  }
)
Tabs.displayName = "Tabs"

// TabsList component
const TabsList = forwardRef<
  HTMLDivElement,
  {
    className?: string
    activeValue?: string
    onValueChange?: (value: string) => void
    children?: ReactNode
    showHoverEffect?: boolean
    showActiveIndicator?: boolean
    activeIndicatorPosition?: "top" | "bottom"
    activeIndicatorOffset?: number
    size?: "sm" | "md" | "lg"
    variant?: "default" | "pills" | "underlined"
    stretch?: boolean
    ariaLabel?: string
    showBottomBorder?: boolean
    bottomBorderClassName?: string
    activeIndicatorClassName?: string
    hoverIndicatorClassName?: string
  }
>(
  (
    {
      className,
      activeValue,
      onValueChange,
      children,
      showHoverEffect = true,
      showActiveIndicator = true,
      activeIndicatorPosition = "bottom",
      activeIndicatorOffset = 0,
      size = "sm",
      variant = "default",
      stretch = false,
      ariaLabel = "Tabs",
      showBottomBorder = false,
      bottomBorderClassName,
      activeIndicatorClassName,
      hoverIndicatorClassName,
      ...props
    },
    ref
  ) => {
    const [hoveredIndex, setHoveredIndex] = useState<number | null>(null)
    const [hoverStyle, setHoverStyle] = useState({})
    const [activeStyle, setActiveStyle] = useState({
      left: "0px",
      width: "0px",
    })
    const tabRefs = useRef<(HTMLDivElement | null)[]>([])
    const scrollContainerRef = useRef<HTMLDivElement | null>(null)

    // Find active tab index based on value
    const activeIndex = React.Children.toArray(children).findIndex(
      (child) =>
        React.isValidElement(child) &&
        (child as React.ReactElement<{ value: string }>).props.value ===
          activeValue
    )

    // Update hover indicator position
    useEffect(() => {
      if (hoveredIndex !== null && showHoverEffect) {
        const hoveredElement = tabRefs.current[hoveredIndex]
        if (hoveredElement) {
          const { offsetLeft, offsetWidth } = hoveredElement
          setHoverStyle({
            left: `${offsetLeft}px`,
            width: `${offsetWidth}px`,
          })
        }
      }
    }, [hoveredIndex, showHoverEffect])

    // Update active indicator position
    const updateActiveIndicator = useCallback(() => {
      if (showActiveIndicator && activeIndex >= 0) {
        const activeElement = tabRefs.current[activeIndex]
        if (activeElement) {
          const { offsetLeft, offsetWidth } = activeElement
          setActiveStyle({
            left: `${offsetLeft}px`,
            width: `${offsetWidth}px`,
          })
        }
      }
    }, [activeIndex, showActiveIndicator])

    // Update active indicator on active tab change
    useEffect(() => {
      updateActiveIndicator()
    }, [activeIndex, updateActiveIndicator])

    // Initialize active indicator position
    useEffect(() => {
      requestAnimationFrame(updateActiveIndicator)
    }, [updateActiveIndicator])

    // Function to scroll tab to center
    const scrollTabToCenter = (index: number) => {
      const tabElement = tabRefs.current[index]
      const scrollContainer = scrollContainerRef.current

      if (tabElement && scrollContainer) {
        const containerWidth = scrollContainer.offsetWidth
        const tabWidth = tabElement.offsetWidth
        const tabLeft = tabElement.offsetLeft

        // Calculate position to center the tab
        const scrollTarget = tabLeft - containerWidth / 2 + tabWidth / 2

        // Smooth scroll to the target position
        scrollContainer.scrollTo({
          left: scrollTarget,
          behavior: "smooth",
        })
      }
    }

    // Size classes
    const sizeClasses = {
      sm: "h-[32px] text-sm",
      md: "h-[40px] text-base",
      lg: "h-[48px] text-lg",
    }

    // Variant classes
    const variantClasses = {
      default: "",
      pills: "rounded-full",
      underlined: "",
    }

    // Active indicator classes
    const activeIndicatorClasses = {
      default: "h-[4px] bg-primary dark:bg-primary",
      pills: "hidden",
      underlined: "h-[4px] bg-primary dark:bg-primary",
    }

    // Hover indicator classes
    const hoverIndicatorClasses = {
      default: "bg-muted dark:bg-muted rounded-[6px]",
      pills: "bg-muted dark:bg-muted rounded-full",
      underlined: "bg-muted dark:bg-muted rounded-[6px]",
    }

    // Create a proper ref callback function
    const setTabRef = useCallback(
      (el: HTMLDivElement | null, index: number) => {
        tabRefs.current[index] = el
      },
      []
    )

    // Save a reference to the scroll container when it's mounted
    const handleScrollableRef = useCallback((node: HTMLDivElement | null) => {
      if (node) {
        // Find the actual scrollable div inside XScrollable
        const scrollableDiv = node.querySelector(
          'div[class*="overflow-x-auto"]'
        )
        if (scrollableDiv) {
          scrollContainerRef.current = scrollableDiv as HTMLDivElement
        }
      }
    }, [])

    // Center the active tab on initial render only
    useEffect(() => {
      if (activeIndex >= 0) {
        // Use a small timeout to ensure the tabs are properly rendered
        const timer = setTimeout(() => {
          scrollTabToCenter(activeIndex)
        }, 100)

        return () => clearTimeout(timer)
      }
    }, []) // Empty dependency array means this only runs once on mount

    return (
      <div
        ref={handleScrollableRef}
        className={cn("relative", className)}
        role="tablist"
        aria-label={ariaLabel}
        {...props}
      >
        <XScrollable showScrollbar={false}>
          <div className={cn("relative", showBottomBorder && "pb-px")}>
            {/* Bottom border for the entire tab list */}
            {showBottomBorder && (
              <div
                className={cn(
                  "bg-border dark:bg-border absolute right-0 bottom-0 left-0 h-px",
                  bottomBorderClassName
                )}
              />
            )}

            {/* Hover Indicator */}
            {showHoverEffect && (
              <div
                className={cn(
                  "absolute z-0 flex items-center transition-all duration-300 ease-out",
                  sizeClasses[size],
                  hoverIndicatorClasses[variant],
                  hoverIndicatorClassName
                )}
                style={{
                  ...hoverStyle,
                  opacity: hoveredIndex !== null ? 1 : 0,
                  transition: "all 300ms ease-out",
                }}
                aria-hidden="true"
              />
            )}

            {/* Tabs */}
            <div
              ref={ref}
              className={cn(
                "relative flex items-center",
                stretch ? "w-full" : "",
                variant === "default" ? "space-x-[6px]" : "space-x-[2px]"
              )}
            >
              {React.Children.map(children, (child, index) => {
                if (!React.isValidElement(child)) return child

                const props = (
                  child as React.ReactElement<{
                    value: string
                    disabled?: boolean
                    label?: string
                    className?: string
                    activeClassName?: string
                    inactiveClassName?: string
                    disabledClassName?: string
                  }>
                ).props

                const { value, disabled } = props
                const isActive = value === activeValue

                return (
                  <div
                    key={value}
                    ref={(el) => setTabRef(el, index)}
                    className={cn(
                      "mb-2 cursor-pointer px-3 py-2 transition-colors duration-300 sm:mb-1.5",
                      sizeClasses[size],
                      variant === "pills" && isActive
                        ? "rounded-full bg-[#0e0f1114] dark:bg-[#ffffff1a]"
                        : "",
                      disabled ? "cursor-not-allowed opacity-50" : "",
                      stretch ? "flex-1 text-center" : "",
                      isActive
                        ? props.activeClassName ||
                            "text-foreground dark:text-foreground"
                        : props.inactiveClassName ||
                            "text-muted-foreground dark:text-muted-foreground",
                      disabled && props.disabledClassName,
                      variantClasses[variant],
                      props.className
                    )}
                    onMouseEnter={() => !disabled && setHoveredIndex(index)}
                    onMouseLeave={() => setHoveredIndex(null)}
                    onClick={() => {
                      if (!disabled) {
                        onValueChange?.(value)
                        scrollTabToCenter(index)
                      }
                    }}
                    role="tab"
                    aria-selected={isActive}
                    aria-disabled={disabled}
                    aria-controls={`tabpanel-${value}`}
                    id={`tab-${value}`}
                    tabIndex={isActive ? 0 : -1}
                  >
                    <div className="flex h-full items-center justify-center whitespace-nowrap">
                      {child}
                    </div>
                  </div>
                )
              })}
            </div>

            {/* Active Indicator */}
            {showActiveIndicator && variant !== "pills" && activeIndex >= 0 && (
              <div
                className={cn(
                  "absolute z-10 transition-all duration-300 ease-out",
                  activeIndicatorClasses[variant],
                  activeIndicatorPosition === "top"
                    ? "top-[-1px]"
                    : "bottom-[-1px]",
                  activeIndicatorClassName
                )}
                style={{
                  ...activeStyle,
                  transition: "all 300ms ease-out z-50",
                  [activeIndicatorPosition]: `${activeIndicatorOffset}px`,
                }}
                aria-hidden="true"
              />
            )}
          </div>
        </XScrollable>
      </div>
    )
  }
)
TabsList.displayName = "TabsList"

// TabsTrigger component
const TabsTrigger = forwardRef<
  HTMLDivElement,
  {
    value: string
    disabled?: boolean
    label?: string
    className?: string
    activeClassName?: string
    inactiveClassName?: string
    disabledClassName?: string
    activeValue?: string
    onValueChange?: (value: string) => void
    children?: ReactNode
  }
>(
  (
    {
      value,
      disabled = false,
      label,
      className,
      activeClassName,
      inactiveClassName,
      disabledClassName,
      children,
      ...props
    },
    ref
  ) => {
    return (
      <div ref={ref} className={className} {...props}>
        {label || children}
      </div>
    )
  }
)

TabsTrigger.displayName = "TabsTrigger"

// TabsContent component
const TabsContent = forwardRef<
  HTMLDivElement,
  {
    value: string
    className?: string
    activeValue?: string
    onValueChange?: (value: string) => void
    children: ReactNode
  }
>(
  (
    {
      value,
      className,
      activeValue,
      onValueChange, // Destructure this prop to prevent it from being passed to the div
      children,
      ...props
    },
    ref
  ) => {
    if (value !== activeValue) return null

    return (
      <div
        ref={ref}
        role="tabpanel"
        id={`tabpanel-${value}`}
        aria-labelledby={`tab-${value}`}
        className={className}
        {...props}
      >
        {children}
      </div>
    )
  }
)
TabsContent.displayName = "TabsContent"

export { Tabs, TabsList, TabsTrigger, TabsContent }

"use client"

import React, { useEffect, useRef } from "react"
import QRCodeStyling from "qr-code-styling"

import { cn } from "@/lib/utils"

interface QRCodeProps {
  value: string
  size?: number
  bgColor?: string // Can be a CSS variable (e.g. --color-background) or a color value
  fgColor?: string // Can be a CSS variable (e.g. --color-foreground) or a color value
  level?: "L" | "M" | "Q" | "H"
  marginSize?: number
  className?: string
  enableNavigation?: boolean // Enable click navigation to the QR code URL
  dotStyle?:
    | "rounded"
    | "dots"
    | "classy"
    | "classy-rounded"
    | "square"
    | "extra-rounded"
  cornerSquareStyle?:
    | "dot"
    | "square"
    | "extra-rounded"
    | "rounded"
    | "dots"
    | "classy"
    | "classy-rounded"
  cornerDotStyle?:
    | "dot"
    | "square"
    | "rounded"
    | "dots"
    | "classy"
    | "classy-rounded"
    | "extra-rounded"
  logoImage?: string // URL or data URI for the logo image
  logoSize?: number // Size of the logo as a percentage of the QR code size (0-1)
  logoMargin?: number // Margin around the logo in pixels
}

// Helper to resolve CSS variable or direct color
function resolveColor(color?: string, fallback?: string) {
  if (!color) return fallback
  if (color.startsWith("--")) {
    if (typeof window !== "undefined") {
      const value = getComputedStyle(document.documentElement).getPropertyValue(
        color
      )
      if (value) return value.trim()
    }
    return fallback
  }
  return color
}

export default function QRCode({
  value,
  size = 200,
  // Use shadcn theme variables for best contrast by default
  bgColor = "--color-background", // background
  fgColor = "--color-foreground", // foreground (text/qr dots)
  level = "M",
  marginSize = 0,
  className,
  enableNavigation = false,
  dotStyle = "square",
  cornerSquareStyle = "square",
  cornerDotStyle = "square",
  logoImage,
  logoSize = 0.2,
  logoMargin = 0,
}: QRCodeProps) {
  const ref = useRef<HTMLDivElement>(null)
  const qrCodeRef = useRef<QRCodeStyling | null>(null)

  useEffect(() => {
    if (!value || !ref.current) return

    // Always resolve to actual color values
    const resolvedBgColor = resolveColor(bgColor, "hsl(0, 0%, 100%)") // fallback: white
    const resolvedFgColor = resolveColor(fgColor, "hsl(222.2, 84%, 4.9%)") // fallback: dark

    const qrCode = new QRCodeStyling({
      width: size,
      height: size,
      type: "svg",
      data: value,
      margin: marginSize,
      image: logoImage,
      qrOptions: {
        typeNumber: 0,
        mode: "Byte",
        errorCorrectionLevel: level,
      },
      imageOptions: {
        hideBackgroundDots: true,
        imageSize: logoSize,
        margin: logoMargin,
        crossOrigin: "anonymous",
      },
      dotsOptions: {
        color: resolvedFgColor,
        type: dotStyle,
      },
      backgroundOptions: {
        color: resolvedBgColor,
      },
      cornersSquareOptions: {
        color: resolvedFgColor,
        type: cornerSquareStyle,
      },
      cornersDotOptions: {
        color: resolvedFgColor,
        type: cornerDotStyle,
      },
    })

    qrCodeRef.current = qrCode

    // Clear previous content
    ref.current.innerHTML = ""

    // Append the QR code
    qrCode.append(ref.current)
  }, [
    value,
    size,
    bgColor,
    fgColor,
    level,
    marginSize,
    dotStyle,
    cornerSquareStyle,
    cornerDotStyle,
    logoImage,
    logoSize,
    logoMargin,
  ])

  // Re-render when theme changes by listening to color changes
  useEffect(() => {
    if (!qrCodeRef.current) return

    const handleThemeChange = () => {
      // Force re-render by updating the effect dependencies
      const resolvedBgColor = resolveColor(bgColor, "hsl(0, 0%, 100%)")
      const resolvedFgColor = resolveColor(fgColor, "hsl(222.2, 84%, 4.9%)")

      qrCodeRef.current?.update({
        dotsOptions: {
          color: resolvedFgColor,
          type: dotStyle,
        },
        backgroundOptions: {
          color: resolvedBgColor,
        },
        cornersSquareOptions: {
          color: resolvedFgColor,
          type: cornerSquareStyle,
        },
        cornersDotOptions: {
          color: resolvedFgColor,
          type: cornerDotStyle,
        },
      })
    }

    // Listen for theme changes
    const mediaQuery = window.matchMedia("(prefers-color-scheme: dark)")
    mediaQuery.addEventListener("change", handleThemeChange)

    // Also listen for class changes on document element (for manual theme switching)
    const observer = new MutationObserver(handleThemeChange)
    observer.observe(document.documentElement, {
      attributes: true,
      attributeFilter: ["class", "data-theme"],
    })

    return () => {
      mediaQuery.removeEventListener("change", handleThemeChange)
      observer.disconnect()
    }
  }, [bgColor, fgColor, dotStyle, cornerSquareStyle, cornerDotStyle])

  if (!value) {
    return (
      <div
        className={cn(
          "flex items-center justify-center rounded-xl border-2 border-dashed border-gray-300 text-gray-500",
          className
        )}
        style={{ width: size, height: size }}
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 368 368"
          className="animate-pulse rounded-lg opacity-30"
          style={{
            width: `${size - marginSize * 8}px`,
            height: `${size - marginSize * 8}px`,
          }}
        >
          <path
            d="m 16,16 0,16 0,16 0,16 0,16 0,16 0,16 0,16 16,0 16,0 16,0 16,0 16,0 16,0 16,0 0,-16 0,-16 0,-16 0,-16 0,-16 0,-16 0,-16 -16,0 -16,0 -16,0 -16,0 -16,0 -16,0 -16,0 z m 128,0 0,16 0,16 16,0 0,-16 16,0 0,-16 -16,0 -16,0 z m 32,16 0,16 16,0 0,-16 -16,0 z m 16,16 0,16 16,0 16,0 0,-16 0,-16 0,-16 -16,0 0,16 0,16 -16,0 z m 0,16 -16,0 -16,0 -16,0 0,16 16,0 16,0 0,16 -16,0 0,16 16,0 0,16 16,0 0,-16 16,0 0,16 -16,0 0,16 -16,0 0,16 16,0 16,0 0,16 16,0 0,-16 0,-16 0,-16 0,-16 0,-16 -16,0 0,-16 -16,0 0,-16 z m 16,112 -16,0 0,16 -16,0 0,16 0,16 16,0 0,16 0,16 -16,0 -16,0 0,-16 16,0 0,-16 -16,0 0,-16 0,-16 -16,0 0,-16 16,0 0,16 16,0 0,-16 0,-16 -16,0 -16,0 0,-16 -16,0 -16,0 -16,0 0,16 -16,0 0,16 -16,0 0,-16 16,0 0,-16 -16,0 -16,0 0,16 -16,0 0,16 0,16 0,16 16,0 0,-16 16,0 16,0 16,0 0,-16 16,0 0,-16 16,0 0,16 -16,0 0,16 16,0 0,16 -16,0 0,16 16,0 16,0 0,16 0,16 0,16 16,0 0,16 16,0 16,0 16,0 0,16 -16,0 -16,0 -16,0 0,-16 -16,0 0,16 0,16 16,0 0,16 -16,0 0,16 16,0 16,0 0,-16 16,0 0,16 16,0 16,0 16,0 16,0 0,-16 16,0 0,16 16,0 16,0 16,0 0,-16 -16,0 -16,0 0,-16 -16,0 0,-16 -16,0 0,16 -16,0 -16,0 0,16 -16,0 0,-16 16,0 0,-16 0,-16 0,-16 16,0 0,-16 -16,0 -16,0 0,-16 16,0 0,-16 0,-16 0,-16 -16,0 0,-16 z m 48,128 0,-16 -16,0 0,16 16,0 z m 32,16 16,0 16,0 0,-16 -16,0 -16,0 0,16 z m 32,-16 16,0 0,-16 0,-16 0,-16 0,-16 -16,0 -16,0 -16,0 0,-16 -16,0 0,16 -16,0 0,16 0,16 16,0 0,-16 16,0 0,16 0,16 16,0 16,0 0,16 z m -48,-80 0,-16 -16,0 -16,0 0,16 16,0 16,0 z m 16,0 16,0 0,-16 0,-16 0,-16 16,0 0,16 16,0 0,16 16,0 0,-16 0,-16 -16,0 0,-16 16,0 0,-16 -16,0 -16,0 0,16 -16,0 0,-16 -16,0 0,16 -16,0 0,16 16,0 0,16 0,16 0,16 z m -16,-48 -16,0 0,16 16,0 0,-16 z m 64,32 -16,0 0,16 16,0 0,-16 z m -224,0 0,-16 -16,0 0,16 16,0 z m -16,0 -16,0 -16,0 -16,0 0,16 16,0 16,0 16,0 0,-16 z m -64,0 -16,0 0,16 16,0 0,-16 z m 0,-48 0,-16 -16,0 0,16 16,0 z m 112,-16 16,0 0,-16 0,-16 -16,0 0,16 0,16 z m 96,-128 0,16 0,16 0,16 0,16 0,16 0,16 0,16 16,0 16,0 16,0 16,0 16,0 16,0 16,0 0,-16 0,-16 0,-16 0,-16 0,-16 0,-1