"use client"

import * as React from "react"
import { useConversation } from "@elevenlabs/react"
import {
  ArrowUpIcon,
  ChevronDown,
  Keyboard,
  Mic,
  MicOff,
  PhoneIcon,
  XIcon,
} from "lucide-react"

import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"
import { Card } from "@/components/ui/card"
import { LiveWaveform } from "@/components/ui/live-waveform"
import { Separator } from "@/components/ui/separator"
import { Textarea } from "@/components/ui/textarea"

export interface ConversationBarProps {
  /**
   * ElevenLabs Agent ID to connect to
   */
  agentId: string

  /**
   * Custom className for the container
   */
  className?: string

  /**
   * Custom className for the waveform
   */
  waveformClassName?: string

  /**
   * Callback when conversation connects
   */
  onConnect?: () => void

  /**
   * Callback when conversation disconnects
   */
  onDisconnect?: () => void

  /**
   * Callback when an error occurs
   */
  onError?: (error: Error) => void

  /**
   * Callback when a message is received
   */
  onMessage?: (message: { source: "user" | "ai"; message: string }) => void

  /**
   * Callback when user sends a message
   */
  onSendMessage?: (message: string) => void
}

export const ConversationBar = React.forwardRef<
  HTMLDivElement,
  ConversationBarProps
>(
  (
    {
      agentId,
      className,
      waveformClassName,
      onConnect,
      onDisconnect,
      onError,
      onMessage,
      onSendMessage,
    },
    ref
  ) => {
    const [isMuted, setIsMuted] = React.useState(false)
    const [agentState, setAgentState] = React.useState<
      "disconnected" | "connecting" | "connected" | "disconnecting" | null
    >("disconnected")
    const [keyboardOpen, setKeyboardOpen] = React.useState(false)
    const [textInput, setTextInput] = React.useState("")
    const mediaStreamRef = React.useRef<MediaStream | null>(null)

    const conversation = useConversation({
      onConnect: () => {
        onConnect?.()
      },
      onDisconnect: () => {
        setAgentState("disconnected")
        onDisconnect?.()
        setKeyboardOpen(false)
      },
      onMessage: (message) => {
        onMessage?.(message)
      },
      micMuted: isMuted,
      onError: (error: unknown) => {
        console.error("Error:", error)
        setAgentState("disconnected")
        const errorObj =
          error instanceof Error
            ? error
            : new Error(
                typeof error === "string" ? error : JSON.stringify(error)
              )
        onError?.(errorObj)
      },
    })

    const getMicStream = React.useCallback(async () => {
      if (mediaStreamRef.current) return mediaStreamRef.current

      const stream = await navigator.mediaDevices.getUserMedia({ audio: true })
      mediaStreamRef.current = stream

      return stream
    }, [])

    const startConversation = React.useCallback(async () => {
      try {
        setAgentState("connecting")

        await getMicStream()

        await conversation.startSession({
          agentId,
          connectionType: "webrtc",
          onStatusChange: (status) => setAgentState(status.status),
        })
      } catch (error) {
        console.error("Error starting conversation:", error)
        setAgentState("disconnected")
        onError?.(error as Error)
      }
    }, [conversation, getMicStream, agentId, onError])

    const handleEndSession = React.useCallback(() => {
      conversation.endSession()
      setAgentState("disconnected")

      if (mediaStreamRef.current) {
        mediaStreamRef.current.getTracks().forEach((t) => t.stop())
        mediaStreamRef.current = null
      }
    }, [conversation])

    const toggleMute = React.useCallback(() => {
      setIsMuted((prev) => !prev)
    }, [])

    const handleStartOrEnd = React.useCallback(() => {
      if (agentState === "connected" || agentState === "connecting") {
        handleEndSession()
      } else if (agentState === "disconnected") {
        startConversation()
      }
    }, [agentState, handleEndSession, startConversation])

    const handleSendText = React.useCallback(() => {
      if (!textInput.trim()) return

      const messageToSend = textInput
      conversation.sendUserMessage(messageToSend)
      setTextInput("")
      onSendMessage?.(messageToSend)
    }, [conversation, textInput, onSendMessage])

    const isConnected = agentState === "connected"

    const handleTextChange = React.useCallback(
      (e: React.ChangeEvent<HTMLTextAreaElement>) => {
        const value = e.target.value
        setTextInput(value)

        if (value.trim() && isConnected) {
          conversation.sendContextualUpdate(value)
        }
      },
      [conversation, isConnected]
    )

    const handleKeyDown = React.useCallback(
      (e: React.KeyboardEvent<HTMLTextAreaElement>) => {
        if (e.key === "Enter" && !e.shiftKey) {
          e.preventDefault()
          handleSendText()
        }
      },
      [handleSendText]
    )

    React.useEffect(() => {
      return () => {
        if (mediaStreamRef.current) {
          mediaStreamRef.current.getTracks().forEach((t) => t.stop())
        }
      }
    }, [])

    return (
      <div
        ref={ref}
        className={cn("flex w-full items-end justify-center p-4", className)}
      >
        <Card className="m-0 w-full gap-0 border p-0 shadow-lg">
          <div className="flex flex-col-reverse">
            <div>
              {keyboardOpen && <Separator />}
              <div className="flex items-center justify-between gap-2 p-2">
                <div className="h-8 w-[120px] md:h-10">
                  <div
                    className={cn(
                      "flex h-full items-center gap-2 rounded-md py-1",
                      "bg-foreground/5 text-foreground/70"
                    )}
                  >
                    <div className="h-full flex-1">
                      <div
                        className={cn(
                          "relative flex h-full w-full shrink-0 items-center justify-center overflow-hidden rounded-sm",
                          waveformClassName
                        )}
                      >
                        <LiveWaveform
                          key={
                            agentState === "disconnected" ? "idle" : "active"
                          }
                          active={isConnected && !isMuted}
                          processing={agentState === "connecting"}
                          barWidth={3}
                          barGap={1}
                          barRadius={4}
                          fadeEdges={true}
                          fadeWidth={24}
                          sensitivity={1.8}
                          smoothingTimeConstant={0.85}
                          height={20}
                          mode="static"
                          className={cn(
                            "h-full w-full transition-opacity duration-300",
                            agentState === "disconnected" && "opacity-0"
                          )}
                        />
                        {agentState === "disconnected" && (
                          <div className="absolute inset-0 flex items-center justify-center">
                            <span className="text-foreground/50 text-[10px] font-medium">
                              Customer Support
                            </span>
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                </div>
                <div className="flex items-center">
                  <Button
                    variant="ghost"
                    size="icon"
                    onClick={toggleMute}
                    aria-pressed={isMuted}
                    className={cn(isMuted ? "bg-foreground/5" : "")}
                    disabled={!isConnected}
                  >
                    {isMuted ? <MicOff /> : <Mic />}
                  </Button>
                  <Button
                    variant="ghost"
                    size="icon"
                    onClick={() => setKeyboardOpen((v) => !v)}
                    aria-pressed={keyboardOpen}
                    className="relative"
                    disabled={!isConnected}
                  >
                    <Keyboard
                      className={
                        "h-5 w-5 transform-gpu transition-all duration-200 ease-[cubic-bezier(0.22,1,0.36,1)] " +
                        (keyboardOpen
                          ? "scale-75 opacity-0"
                          : "scale-100 opacity-100")
                      }
                    />
                    <ChevronDown
                      className={
                        "absolute inset-0 m-auto h-5 w-5 transform-gpu transition-all delay-50 duration-200 ease-[cubic-bezier(0.34,1.56,0.64,1)] " +
                        (keyboardOpen
                          ? "scale-100 opacity-100"
                          : "scale-75 opacity-0")
                      }
                    />
                  </Button>
                  <Separator orientation="vertical" className="mx-1 -my-2.5" />
                  <Button
                    variant="ghost"
                    size="icon"
                    onClick={handleStartOrEnd}
                    disabled={agentState === "disconnecting"}
                  >
                    {isConnected || agentState === "connecting" ? (
                      <XIcon className="h-5 w-5" />
                    ) : (
                      <PhoneIcon className="h-5 w-5" />
                    )}
                  </Button>
                </div>
              </div>
            </div>

            <div
              className={cn(
                "overflow-hidden transition-all duration-300 ease-out",
                keyboardOpen ? "max-h-[120px]" : "max-h-0"
              )}
            >
              <div className="relative px-2 pt-2 pb-2">
                <Textarea
                  value={textInput}
                  onChange={handleTextChange}
                  onKeyDown={handleKeyDown}
                  placeholder="Enter your message..."
                  className="min-h-[100px] resize-none border-0 pr-12 shadow-none focus-visible:ring-0"
                  disabled={!isConnected}
                />
                <Button
                  size="icon"
                  variant="ghost"
                  onClick={handleSendText}
                  disabled={!textInput.trim() || !isConnected}
                  className="absolute right-3 bottom-3 h-8 w-8"
                >
                  <ArrowUpIcon className="h-4 w-4" />
                </Button>
              </div>
            </div>
          </div>
        </Card>
      </div>
    )
  }
)

ConversationBar.displayName = "ConversationBar"

"use client"

import { useEffect, useRef, useState, type ReactNode } from "react"

import { cn } from "@/lib/utils"

interface XScrollableProps {
  children: ReactNode
  className?: string
  showScrollbar?: boolean
  scrollbarTrackClassName?: string
  scrollbarThumbClassName?: string
  onScroll?: (
    scrollLeft: number,
    scrollWidth: number,
    clientWidth: number
  ) => void
}

export function XScrollable({
  children,
  className,
  showScrollbar = false,
  scrollbarTrackClassName,
  scrollbarThumbClassName,
  onScroll,
}: XScrollableProps) {
  const scrollContainerRef = useRef<HTMLDivElement>(null)
  const [showLeftShadow, setShowLeftShadow] = useState(false)
  const [showRightShadow, setShowRightShadow] = useState(false)

  useEffect(() => {
    const scrollContainer = scrollContainerRef.current
    if (!scrollContainer) return

    const handleScroll = () => {
      const { scrollLeft, scrollWidth, clientWidth } = scrollContainer
      setShowLeftShadow(scrollLeft > 0)
      setShowRightShadow(scrollLeft < scrollWidth - clientWidth)
      onScroll?.(scrollLeft, scrollWidth, clientWidth)
    }

    handleScroll() // Initial check
    scrollContainer.addEventListener("scroll", handleScroll)

    return () => {
      scrollContainer.removeEventListener("scroll", handleScroll)
    }
  }, [onScroll])

  return (
    <div className="relative">
      <div
        ref={scrollContainerRef}
        className={cn(
          "overflow-x-auto overflow-y-hidden",
          showScrollbar
            ? "scrollbar-thin scrollbar-track-transparent scrollbar-thumb-gray-300 hover:scrollbar-thumb-gray-400"
            : "scrollbar-hide",
          className
        )}
      >
        {children}
      </div>
      {showLeftShadow && (
        <div className="from-background pointer-events-none absolute top-0 bottom-0 left-0 w-8 bg-gradient-to-r to-transparent" />
      )}
      {showRightShadow && (
        <div className="from-background pointer-events-none absolute top-0 right-0 bottom-0 w-8 bg-gradient-to-l to-transparent" />
      )}
      {showScrollbar && (
        <div
          className={cn(
            "mt-1 h-1 rounded-full bg-gray-200",
            scrollbarTrackClassName
          )}
        >
          <div
            className={cn(
              "h-full rounded-full bg-gray-400",
              scrollbarThumbClassName
            )}
            style={{
              width: scrollContainerRef.current
                ? `${(scrollContainerRef.current.clientWidth / scrollContainerRef.current.scrollWidth) * 100}%`
                : "0%",
              transform: scrollContainerRef.current
                ? `translateX(${(scrollContainerRef.current.scrollLeft / scrollContainerRef.current.clientWidth) * 100}%)`
                : "translateX(0%)",
            }}
          />
        </div>
      )}
    </div>
  )
}

"use client"

import React, {
  forwardRef,
  useCallback,
  useEffect,
  useRef,
  useState,
  type ReactNode,
} from "react"

import { cn } from "@/lib/utils"
import { XScrollable } from "@/components/ui/x-scrollable"

// Base Tabs component (Root)
const Tabs = forwardRef<
  HTMLDivElement,
  {
    defaultValue?: string
    value?: string
    onValueChange?: (value: string) => void
    className?: string
    children?: ReactNode
  }
>(
  (
    { defaultValue, value, onValueChange, className, children, ...props },
    ref
  ) => {
    const [activeValue, setActiveValue] = useState(value || defaultValue || "")

    useEffect(() => {
      if (value !== undefined) {
        setActiveValue(value)
      }
    }, [value])

    const handleValueChange = useCallback(
      (newValue: string) => {
        if (value === undefined) {
          setActiveValue(newValue)
        }
        onValueChange?.(newValue)
      },
      [onValueChange, value]
    )

    return (
      <div ref={ref} className={cn("tabs-container", className)} {...props}>
        {React.Children.map(children, (child) => {
          if (!React.isValidElement(child)) return child

          // Only pass tab-related props to tab components
          const childType = child.type
          const isTabComponent =
            childType === TabsList ||
            childType === TabsTrigger ||
            childType === TabsContent ||
            (typeof childType === "function" &&
              ((childType as any).displayName === "TabsList" ||
                (childType as any).displayName === "TabsTrigger" ||
                (childType as any).displayName === "TabsContent"))

          if (isTabComponent) {
            return React.cloneElement(
              child as React.ReactElement<{
                activeValue?: string
                onValueChange?: (value: string) => void
                className?: string
                children?: ReactNode
                [key: string]: any
              }>,
              {
                activeValue,
                onValueChange: handleValueChange,
              }
            )
          }

          return child
        })}
      </div>
    )
  }
)
Tabs.displayName = "Tabs"

// TabsList component
const TabsList = forwardRef<
  HTMLDivElement,
  {
    className?: string
    activeValue?: string
    onValueChange?: (value: string) => void
    children?: ReactNode
    showHoverEffect?: boolean
    showActiveIndicator?: boolean
    activeIndicatorPosition?: "top" | "bottom"
    activeIndicatorOffset?: number
    size?: "sm" | "md" | "lg"
    variant?: "default" | "pills" | "underlined"
    stretch?: boolean
    ariaLabel?: string
    showBottomBorder?: boolean
    bottomBorderClassName?: string
    activeIndicatorClassName?: string
    hoverIndicatorClassName?: string
  }
>(
  (
    {
      className,
      activeValue,
      onValueChange,
      children,
      showHoverEffect = true,
      showActiveIndicator = true,
      activeIndicatorPosition = "bottom",
      activeIndicatorOffset = 0,
      size = "sm",
      variant = "default",
      stretch = false,
      ariaLabel = "Tabs",
      showBottomBorder = false,
      bottomBorderClassName,
      activeIndicatorClassName,
      hoverIndicatorClassName,
      ...props
    },
    ref
  ) => {
    const [hoveredIndex, setHoveredIndex] = useState<number | null>(null)
    const [hoverStyle, setHoverStyle] = useState({})
    const [activeStyle, setActiveStyle] = useState({
      left: "0px",
      width: "0px",
    })
    const tabRefs = useRef<(HTMLDivElement | null)[]>([])
    const scrollContainerRef = useRef<HTMLDivElement | null>(null)

    // Find active tab index based on value
    const activeIndex = React.Children.toArray(children).findIndex(
      (child) =>
        React.isValidElement(child) &&
        (child as React.ReactElement<{ value: string }>).props.value ===
          activeValue
    )

    // Update hover indicator position
    useEffect(() => {
      if (hoveredIndex !== null && showHoverEffect) {
        const hoveredElement = tabRefs.current[hoveredIndex]
        if (hoveredElement) {
          const { offsetLeft, offsetWidth } = hoveredElement
          setHoverStyle({
            left: `${offsetLeft}px`,
            width: `${offsetWidth}px`,
          })
        }
      }
    }, [hoveredIndex, showHoverEffect])

    // Update active indicator position
    const updateActiveIndicator = useCallback(() => {
      if (showActiveIndicator && activeIndex >= 0) {
        const activeElement = tabRefs.current[activeIndex]
        if (activeElement) {
          const { offsetLeft, offsetWidth } = activeElement
          setActiveStyle({
            left: `${offsetLeft}px`,
            width: `${offsetWidth}px`,
          })
        }
      }
    }, [activeIndex, showActiveIndicator])

    // Update active indicator on active tab change
    useEffect(() => {
      updateActiveIndicator()
    }, [activeIndex, updateActiveIndicator])

    // Initialize active indicator position
    useEffect(() => {
      requestAnimationFrame(updateActiveIndicator)
    }, [updateActiveIndicator])

    // Function to scroll tab to center
    const scrollTabToCenter = (index: number) => {
      const tabElement = tabRefs.current[index]
      const scrollContainer = scrollContainerRef.current

      if (tabElement && scrollContainer) {
        const containerWidth = scrollContainer.offsetWidth
        const tabWidth = tabElement.offsetWidth
        const tabLeft = tabElement.offsetLeft

        // Calculate position to center the tab
        const scrollTarget = tabLeft - containerWidth / 2 + tabWidth / 2

        // Smooth scroll to the target position
        scrollContainer.scrollTo({
          left: scrollTarget,
          behavior: "smooth",
        })
      }
    }

    // Size classes
    const sizeClasses = {
      sm: "h-[32px] text-sm",
      md: "h-[40px] text-base",
      lg: "h-[48px] text-lg",
    }

    // Variant classes
    const variantClasses = {
      default: "",
      pills: "rounded-full",
      underlined: "",
    }

    // Active indicator classes
    const activeIndicatorClasses = {
      default: "h-[4px] bg-primary dark:bg-primary",
      pills: "hidden",
      underlined: "h-[4px] bg-primary dark:bg-primary",
    }

    // Hover indicator classes
    const hoverIndicatorClasses = {
      default: "bg-muted dark:bg-muted rounded-[6px]",
      pills: "bg-muted dark:bg-muted rounded-full",
      underlined: "bg-muted dark:bg-muted rounded-[6px]",
    }

    // Create a proper ref callback function
    const setTabRef = useCallback(
      (el: HTMLDivElement | null, index: number) => {
        tabRefs.current[index] = el
      },
      []
    )

    // Save a reference to the scroll container when it's mounted
    const handleScrollableRef = useCallback((node: HTMLDivElement | null) => {
      if (node) {
        // Find the actual scrollable div inside XScrollable
        const scrollableDiv = node.querySelector(
          'div[class*="overflow-x-auto"]'
        )
        if (scrollableDiv) {
          scrollContainerRef.current = scrollableDiv as HTMLDivElement
        }
      }
    }, [])

    // Center the active tab on initial render only
    useEffect(() => {
      if (activeIndex >= 0) {
        // Use a small timeout to ensure the tabs are properly rendered
        const timer = setTimeout(() => {
          scrollTabToCenter(activeIndex)
        }, 100)

        return () => clearTimeout(timer)
      }
    }, []) // Empty dependency array means this only runs once on mount

    return (
      <div
        ref={handleScrollableRef}
        className={cn("relative", className)}
        role="tablist"
        aria-label={ariaLabel}
        {...props}
      >
        <XScrollable showScrollbar={false}>
          <div className={cn("relative", showBottomBorder && "pb-px")}>
            {/* Bottom border for the entire tab list */}
            {showBottomBorder && (
              <div
                className={cn(
                  "bg-border dark:bg-border absolute right-0 bottom-0 left-0 h-px",
                  bottomBorderClassName
                )}
              />
            )}

            {/* Hover Indicator */}
            {showHoverEffect && (
              <div
                className={cn(
                  "absolute z-0 flex items-center transition-all duration-300 ease-out",
                  sizeClasses[size],
                  hoverIndicatorClasses[variant],
                  hoverIndicatorClassName
                )}
                style={{
                  ...hoverStyle,
                  opacity: hoveredIndex !== null ? 1 : 0,
                  transition: "all 300ms ease-out",
                }}
                aria-hidden="true"
              />
            )}

            {/* Tabs */}
            <div
              ref={ref}
              className={cn(
                "relative flex items-center",
                stretch ? "w-full" : "",
                variant === "default" ? "space-x-[6px]" : "space-x-[2px]"
              )}
            >
              {React.Children.map(children, (child, index) => {
                if (!React.isValidElement(child)) return child

                const props = (
                  child as React.ReactElement<{
                    value: string
                    disabled?: boolean
                    label?: string
                    className?: string
                    activeClassName?: string
                    inactiveClassName?: string
                    disabledClassName?: string
                  }>
                ).props

                const { value, disabled } = props
                const isActive = value === activeValue

                return (
                  <div
                    key={value}
                    ref={(el) => setTabRef(el, index)}
                    className={cn(
                      "mb-2 cursor-pointer px-3 py-2 transition-colors duration-300 sm:mb-1.5",
                      sizeClasses[size],
                      variant === "pills" && isActive
                        ? "rounded-full bg-[#0e0f1114] dark:bg-[#ffffff1a]"
                        : "",
                      disabled ? "cursor-not-allowed opacity-50" : "",
                      stretch ? "flex-1 text-center" : "",
                      isActive
                        ? props.activeClassName ||
                            "text-foreground dark:text-foreground"
                        : props.inactiveClassName ||
                            "text-muted-foreground dark:text-muted-foreground",
                      disabled && props.disabledClassName,
                      variantClasses[variant],
                      props.className
                    )}
                    onMouseEnter={() => !disabled && setHoveredIndex(index)}
                    onMouseLeave={() => setHoveredIndex(null)}
                    onClick={() => {
                      if (!disabled) {
                        onValueChange?.(value)
                        scrollTabToCenter(index)
                      }
                    }}
                    role="tab"
                    aria-selected={isActive}
                    aria-disabled={disabled}
                    aria-controls={`tabpanel-${value}`}
                    id={`tab-${value}`}
                    tabIndex={isActive ? 0 : -1}
                  >
                    <div className="flex h-full items-center justify-center whitespace-nowrap">
                      {child}
                    </div>
                  </div>
                )
              })}
            </div>

            {/* Active Indicator */}
            {showActiveIndicator && variant !== "pills" && activeIndex >= 0 && (
              <div
                className={cn(
                  "absolute z-10 transition-all duration-300 ease-out",
                  activeIndicatorClasses[variant],
                  activeIndicatorPosition === "top"
                    ? "top-[-1px]"
                    : "bottom-[-1px]",
                  activeIndicatorClassName
                )}
                style={{
                  ...activeStyle,
                  transition: "all 300ms ease-out z-50",
                  [activeIndicatorPosition]: `${activeIndicatorOffset}px`,
                }}
                aria-hidden="true"
              />
            )}
          </div>
        </XScrollable>
      </div>
    )
  }
)
TabsList.displayName = "TabsList"

// TabsTrigger component
const TabsTrigger = forwardRef<
  HTMLDivElement,
  {
    value: string
    disabled?: boolean
    label?: string
    className?: string
    activeClassName?: string
    inactiveClassName?: string
    disabledClassName?: string
    activeValue?: string
    onValueChange?: (value: string) => void
    children?: ReactNode
  }
>(
  (
    {
      value,
      disabled = false,
      label,
      className,
      activeClassName,
      inactiveClassName,
      disabledClassName,
      children,
      ...props
    },
    ref
  ) => {
    return (
      <div ref={ref} className={className} {...props}>
        {label || children}
      </div>
    )
  }
)

TabsTrigger.displayName = "TabsTrigger"

// TabsContent component
const TabsContent = forwardRef<
  HTMLDivElement,
  {
    value: string
    className?: string
    activeValue?: string
    onValueChange?: (value: string) => void
    children: ReactNode
  }
>(
  (
    {
      value,
      className,
      activeValue,
      onValueChange, // Destructure this prop to prevent it from being passed to the div
      children,
      ...props
    },
    ref
  ) => {
    if (value !== activeValue) return null

    return (
      <div
        ref={ref}
        role="tabpanel"
        id={`tabpanel-${value}`}
        aria-labelledby={`tab-${value}`}
        className={className}
        {...props}
      >
        {children}
      </div>
    )
  }
)
TabsContent.displayName = "TabsContent"

export { Tabs, TabsList, TabsTrigger, TabsContent }
