"use client"

import * as React from "react"
import Link from "next/link"
import { usePathname } from "next/navigation"
import { Plus } from "phosphor-react"

import { cn } from "@/lib/utils"

interface Route {
  href: string
  label: string
  icon: React.ComponentType<any>
}

interface BarItemProps {
  href: string
  label: string
  Icon?: React.ComponentType<any>
  isActive: boolean
  labels: boolean
  onItemClick: (href: string) => void
}

function usePressAnimation(duration = 100) {
  const [isPressed, setIsPressed] = React.useState(false)
  const timeoutRef = React.useRef<NodeJS.Timeout | null>(null)

  const triggerPress = React.useCallback(() => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current)
    }

    setIsPressed(true)

    // Immediately start returning to original size after duration
    timeoutRef.current = setTimeout(() => {
      setIsPressed(false)
    }, duration)
  }, [duration])

  React.useEffect(() => {
    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current)
      }
    }
  }, [])

  return { isPressed, triggerPress }
}

const BarItem = React.memo<BarItemProps>(
  ({ href, label, Icon, isActive, labels, onItemClick }) => {
    const { isPressed, triggerPress } = usePressAnimation(100)

    const handleClick = () => {
      triggerPress()
      onItemClick(href)
    }

    return (
      <li className="flex-1">
        <Link
          href={href}
          className={cn(
            "flex h-full w-full flex-col items-center justify-center px-1 transition-colors duration-150",
            labels ? "py-2" : "py-3"
          )}
          onClick={handleClick}
        >
          <div className="flex flex-col items-center">
            {Icon && (
              <div
                className="transform transition-transform duration-100"
                style={{
                  transform: isPressed ? "scale(0.9)" : "scale(1)",
                  transitionTimingFunction: "cubic-bezier(.08,.52,.52,1)",
                }}
              >
                <Icon
                  className={cn(
                    "h-7 w-7 transition-colors duration-150",
                    isActive ? "text-primary" : "text-muted-foreground"
                  )}
                  weight={isActive ? "fill" : "regular"}
                />
              </div>
            )}
            {labels && (
              <span
                className={cn(
                  "mt-1.5 text-center text-[10px] leading-tight transition-colors duration-150",
                  isActive ? "text-primary" : "text-muted-foreground"
                )}
              >
                {label}
              </span>
            )}
          </div>
        </Link>
      </li>
    )
  }
)

BarItem.displayName = "BarItem"

interface CenterButtonConfig {
  onClick: () => void
  label?: string
}

interface CenterButtonProps {
  onClick: () => void
  label?: string
  labels: boolean
}

const CenterButton = React.memo<CenterButtonProps>(
  ({ onClick, label, labels }) => {
    const { isPressed, triggerPress } = usePressAnimation(100)

    const handleClick = () => {
      triggerPress()
      onClick()
    }

    return (
      <li className="flex-1">
        <button
          onClick={handleClick}
          className="flex h-full w-full flex-col items-center justify-center px-1 py-1 transition-colors duration-150"
          aria-label={label || "Add"}
        >
          <div className="flex flex-col items-center">
            <div
              className="supports-[backdrop-filter]:bg-muted/90 rounded-lg px-6 py-2 backdrop-blur-lg transition-transform duration-100"
              style={{
                transform: isPressed ? "scale(0.92)" : "scale(1)",
                transitionTimingFunction: "cubic-bezier(.08,.52,.52,1)",
              }}
            >
              <Plus
                className="text-muted-foreground h-7 w-7 transition-colors duration-150"
                weight="regular"
              />
            </div>
            {labels && label && (
              <span className="text-accent-foreground mt-1.5 text-center text-[10px] leading-tight">
                {label}
              </span>
            )}
          </div>
        </button>
      </li>
    )
  }
)

CenterButton.displayName = "CenterButton"

interface BottomMobileNavProps {
  routes?: Route[]
  labels?: boolean
  centerButton?: CenterButtonConfig
  className?: string
}

export function BottomMobileNav({
  routes = [],
  labels = false,
  centerButton,
  className,
}: BottomMobileNavProps) {
  const pathname = usePathname()
  const [lastClickedItem, setLastClickedItem] = React.useState<string | null>(
    null
  )
  const [activeRoute, setActiveRoute] = React.useState<string>("")

  // Optimized route detection with minimal DOM queries
  React.useEffect(() => {
    if (routes.length === 0) return

    const updateActiveRoute = () => {
      const hasHashRoutes = routes.some((route) => route.href.startsWith("#"))
      let currentRoute = ""

      if (hasHashRoutes) {
        currentRoute = window.location.hash
        // If no hash, default to first route for hash-based navigation
        if (!currentRoute && routes.length > 0) {
          currentRoute = routes[0].href
        }
      } else {
        currentRoute = pathname
      }

      setActiveRoute(currentRoute || routes[0]?.href || "")
    }

    updateActiveRoute()

    // Use single event listener for both hash and navigation changes
    const handleRouteChange = () => updateActiveRoute()

    window.addEventListener("hashchange", handleRouteChange, { passive: true })
    window.addEventListener("popstate", handleRouteChange, { passive: true })

    return () => {
      window.removeEventListener("hashchange", handleRouteChange)
      window.removeEventListener("popstate", handleRouteChange)
    }
  }, [routes, pathname])

  // Additional effect to handle initial hash on client-side hydration
  React.useEffect(() => {
    if (
      typeof window !== "undefined" &&
      routes.some((route) => route.href.startsWith("#"))
    ) {
      const initialHash = window.location.hash
      if (initialHash) {
        setActiveRoute(initialHash)
      }
    }
  }, [])

  // Optimized click handler with minimal state updates
  const handleItemClick = React.useCallback(
    (href: string) => {
      // Scroll to top optimization for same active item
      if (lastClickedItem === href && activeRoute === href) {
        // Use requestAnimationFrame for smooth scroll timing
        requestAnimationFrame(() => {
          window.scrollTo({
            top: 0,
            behavior: "smooth",
          })
        })
      }
      setLastClickedItem(href)
    },
    [lastClickedItem, activeRoute]
  )

  const handleCenterButtonClick = React.useCallback(() => {
    centerButton?.onClick()
  }, [centerButton])

  // Optimized route splitting with useMemo
  const { firstHalf, secondHalf } = React.useMemo(() => {
    const halfLength = Math.ceil(routes.length / 2)
    return {
      firstHalf: routes.slice(0, halfLength),
      secondHalf: routes.slice(halfLength),
    }
  }, [routes])

  return (
    <nav
      className={cn(
        "fixed right-0 bottom-0 left-0 z-50 block py-1 md:hidden",
        "pt-1 pb-[env(safe-area-inset-bottom,4px)]",
        "supports-[backdrop-filter]:bg-background/85 backdrop-blur-lg",
        className
      )}
    >
      <ul className="relative flex items-center justify-around">
        {centerButton ? (
          <>
            {firstHalf.map(({ href, label, icon: Icon }, index) => (
              <BarItem
                key={`${label}-${index}`}
                href={href}
                label={label}
                Icon={Icon}
                isActive={activeRoute === href}
                labels={labels}
                onItemClick={handleItemClick}
              />
            ))}

            <CenterButton
              onClick={handleCenterButtonClick}
              label={centerButton.label}
              labels={labels}
            />

            {secondHalf.map(({ href, label, icon: Icon }, index) => (
              <BarItem
                key={`${label}-${firstHalf.length + index}`}
                href={href}
                label={label}
                Icon={Icon}
                isActive={activeRoute === href}
                labels={labels}
                onItemClick={handleItemClick}
              />
            ))}
          </>
        ) : (
          routes.map(({ href, label, icon: Icon }, index) => (
            <BarItem
              key={`${label}-${index}`}
              href={href}
              label={label}
              Icon={Icon}
              isActive={activeRoute === href}
              labels={labels}
              onItemClick={handleItemClick}
            />
          ))
        )}
      </ul>
    </nav>
  )
}

"use client"

import React, { memo, useCallback, useRef, useState } from "react"
import Link from "next/link"
import { usePathname } from "next/navigation"
import { Plus } from "phosphor-react"

import { cn } from "@/lib/utils"
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "@/components/ui/tooltip"

interface Route {
  href: string
  label: string
  icon: React.ComponentType<any>
}

interface SidebarProps {
  className?: string
  routes?: Route[]
  showCTAButton?: boolean
  onCTAClick?: () => void
  tooltips?: boolean
}

function usePressAnimation(duration = 100) {
  const [isPressed, setIsPressed] = useState(false)
  const timeoutRef = useRef<NodeJS.Timeout | null>(null)

  const trigger = useCallback(() => {
    if (timeoutRef.current) clearTimeout(timeoutRef.current)
    setIsPressed(true)
    timeoutRef.current = setTimeout(() => setIsPressed(false), duration)
  }, [duration])

  React.useEffect(() => {
    return () => {
      if (timeoutRef.current) clearTimeout(timeoutRef.current)
    }
  }, [])

  return { isPressed, trigger }
}

export const Sidebar = memo(function Sidebar({
  className,
  routes = [],
  showCTAButton = true,
  onCTAClick = () => console.log("Sidebar CTA clicked"),
  tooltips = true,
}: SidebarProps) {
  const pathname = usePathname()
  const [lastClickedItem, setLastClickedItem] = React.useState<string | null>(
    null
  )
  const [activeRoute, setActiveRoute] = React.useState<string>("")
  const [clickedItem, setClickedItem] = React.useState<string | null>(null)

  const pressAnimations = useRef<
    Record<string, ReturnType<typeof usePressAnimation>>
  >({})
  const getAnimation = (key: string) => {
    if (!pressAnimations.current[key]) {
      pressAnimations.current[key] = { isPressed: false, trigger: () => {} }
    }
    return pressAnimations.current[key]
  }

  const [pressedItems, setPressedItems] = useState<Record<string, boolean>>({})
  const timeoutsRef = useRef<Record<string, NodeJS.Timeout>>({})

  const triggerPress = useCallback((key: string) => {
    if (timeoutsRef.current[key]) clearTimeout(timeoutsRef.current[key])
    setPressedItems((prev) => ({ ...prev, [key]: true }))
    timeoutsRef.current[key] = setTimeout(() => {
      setPressedItems((prev) => ({ ...prev, [key]: false }))
    }, 100)
  }, [])

  React.useEffect(() => {
    return () => {
      Object.values(timeoutsRef.current).forEach(clearTimeout)
    }
  }, [])

  React.useEffect(() => {
    if (routes.length > 0) {
      setActiveRoute(routes[0]?.href || "")
      setClickedItem(routes[0]?.href || null)
    }
  }, [routes])

  const handleItemClick = React.useCallback(
    (href: string) => {
      if (lastClickedItem === href && activeRoute === href) {
        requestAnimationFrame(() => {
          window.scrollTo({
            top: 0,
            behavior: "smooth",
          })
        })
      }
      setLastClickedItem(href)
    },
    [lastClickedItem, activeRoute]
  )

  const Logo = () => (
    <svg
      className="size-6"
      viewBox="0 0 24 24"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
    >
      <path
        d="M12 2L2 7L12 12L22 7L12 2Z"
        stroke="currentColor"
        strokeWidth="2"
        strokeLinecap="round"
        strokeLinejoin="round"
      />
      <path
        d="M2 17L12 22L22 17"
        stroke="currentColor"
        strokeWidth="2"
        strokeLinecap="round"
        strokeLinejoin="round"
      />
      <path
        d="M2 12L12 17L22 12"
        stroke="currentColor"
        strokeWidth="2"
        strokeLinecap="round"
        strokeLinejoin="round"
      />
    </svg>
  )

  return (
    <TooltipProvider>
      <nav
        className={cn(
          "bg-background/80 fixed top-0 left-0 z-50 hidden h-screen w-20 flex-col items-center border-r py-3 backdrop-blur-lg md:flex",
          className
        )}
      >
        <div className="mb-8 py-2">
          <Link href="/" className="block" onClick={() => handleItemClick("/")}>
            <Logo />
          </Link>
        </div>

        <div className="flex flex-1 flex-col items-center justify-center space-y-4">
          {routes.slice(0, 2).map(({ href, icon: Icon, label }, index) => {
            const isActive = clickedItem === href
            const linkElement = (
              <Link
                key={href}
                href={href}
                onClick={() => {
                  handleItemClick(href)
                  setClickedItem(href)
                  triggerPress(href)
                }}
                style={{
                  transform: pressedItems[href] ? "scale(0.92)" : "scale(1)",
                  transition: "transform 100ms cubic-bezier(.08,.52,.52,1)",
                }}
                className={cn(
                  "group relative rounded-lg px-4 py-3 transition-colors duration-300",
                  isActive ? "text-accent-foreground" : "text-muted-foreground"
                )}
              >
                <div className="bg-accent absolute inset-0 scale-75 rounded-lg opacity-0 transition-all duration-200 ease-out group-hover:scale-100 group-hover:opacity-100" />
                {Icon && (
                  <Icon
                    className={cn(
                      "relative z-10 h-6 w-6 transition-colors duration-300",
                      isActive && "text-accent-foreground"
                    )}
                    weight={isActive ? "fill" : "regular"}
                  />
                )}
              </Link>
            )

            return tooltips ? (
              <Tooltip key={href}>
                <TooltipTrigger asChild>{linkElement}</TooltipTrigger>
                <TooltipContent side="right" sideOffset={4} hideArrow>
                  {label}
                </TooltipContent>
              </Tooltip>
            ) : (
              <div key={href}>{linkElement}</div>
            )
          })}

          {showCTAButton &&
            (() => {
              const buttonElement = (
                <button
                  key="cta-button"
                  onClick={() => {
                    onCTAClick()
                    triggerPress("cta")
                  }}
                  style={{
                    transform: pressedItems["cta"] ? "scale(0.92)" : "scale(1)",
                    transition: "transform 100ms cubic-bezier(.08,.52,.52,1)",
                  }}
                  className="group bg-muted text-muted-foreground relative cursor-pointer rounded-lg px-4 py-3 transition-colors duration-300"
                >
                  <div className="bg-accent absolute inset-0 scale-75 rounded-lg opacity-0 transition-all duration-200 ease-out group-hover:scale-100 group-hover:opacity-100" />
                  <Plus
                    className="group-hover:text-accent-foreground relative z-10 h-6 w-6 transition-colors duration-300"
                    weight="regular"
                  />
                </button>
              )

              return tooltips ? (
                <Tooltip>
                  <TooltipTrigger asChild>{buttonElement}</TooltipTrigger>
                  <TooltipContent side="right" sideOffset={4} hideArrow>
                    Add
                  </TooltipContent>
                </Tooltip>
              ) : (
                buttonElement
              )
            })()}

          {routes.slice(2).map(({ href, icon: Icon, label }, index) => {
            const isActive = clickedItem === href
            const linkElement = (
              <Link
                key={href}
                href={href}
                onClick={() => {
                  handleItemClick(href)
                  setClickedItem(href)
                  triggerPress(href)
                }}
                style={{
                  transform: pressedItems[href] ? "scale(0.92)" : "scale(1)",
                  transition: "transform 100ms cubic-bezier(.08,.52,.52,1)",
                }}
                className={cn(
                  "group relative rounded-lg px-4 py-3 transition-colors duration-300",
                  isActive ? "text-accent-foreground" : "text-muted-foreground"
                )}
              >
                <div className="bg-accent absolute inset-0 scale-75 rounded-lg opacity-0 transition-all duration-200 ease-out group-hover:scale-100 group-hover:opacity-100" />
                {Icon && (
                  <Icon
                    className={cn(
                      "relative z-10 h-6 w-6 transition-colors duration-300",
                      isActive && "text-accent-foreground"
                    )}
                    weight={isActive ? "fill" : "regular"}
                  />
                )}
              </Link>
            )

            return tooltips ? (
              <Tooltip key={href}>
                <TooltipTrigger asChild>{linkElement}</TooltipTrigger>
                <TooltipContent side="right" sideOffset={4} hideArrow>
                  {label}
                </TooltipContent>
              </Tooltip>
            ) : (
              <div key={href}>{linkElement}</div>
            )
          })}
        </div>

        <div className="mt-auto">
          {/* You can add theme switcher or other controls here */}
        </div>
      </nav>
    </TooltipProvider>
  )
})

Sidebar.displayName = "Sidebar"
