"use client"

import { Marquee } from "@/registry/delta-ui/blocks/marquee"

const brands = [
  {
    name: "Google",
    logo: "/images/brands/google.svg",
    alt: "Google logo",
  },
  {
    name: "Anthropic",
    logo: "/images/brands/anthropic.svg",
    alt: "Anthropic logo",
  },
  {
    name: "Microsoft",
    logo: "/images/brands/microsoft.svg",
    alt: "Microsoft logo",
  },
  {
    name: "Apple",
    logo: "/images/brands/apple.svg",
    alt: "Apple logo",
  },
  {
    name: "NVIDIA",
    logo: "/images/brands/nvidia.svg",
    alt: "NVIDIA logo",
  },
]

export default function BrandMarqueeDemo() {
  return (
    <div className="w-full py-12">
      <Marquee
        speed={20}
        gap={80}
        itemClassName="h-10 opacity-70 [&_svg]:fill-muted-foreground transition-all duration-300 hover:opacity-100 hover:[&_svg]:fill-foreground"
        className="py-4"
      >
        {brands.map((brand) => (
          <img
            key={brand.name}
            src={brand.logo || "/placeholder.svg"}
            alt={brand.alt || brand.name}
            className="h-full w-auto object-contain"
            loading="lazy"
          />
        ))}
      </Marquee>
    </div>
  )
}
