"use client"

import { BrandMarquee } from "@/registry/delta-ui/blocks/brandmarquee"

const brands = [
  {
    name: "Google",
    logo: "/images/brands/google.svg",
    alt: "Google logo",
  },
  {
    name: "Anthropic",
    logo: "/images/brands/anthropic.svg",
    alt: "Anthropic logo",
  },
  {
    name: "Microsoft",
    logo: "/images/brands/microsoft.svg",
    alt: "Microsoft logo",
  },
  {
    name: "Apple",
    logo: "/images/brands/apple.svg",
    alt: "Apple logo",
  },
  {
    name: "NVIDIA",
    logo: "/images/brands/nvidia.svg",
    alt: "NVIDIA logo",
  },
]

export default function BrandMarqueeStackedDemo() {
  return (
    <div className="w-full space-y-8 py-12">
      {/* Top row - moving right to left */}
      <BrandMarquee
        brands={brands}
        direction="left"
        speed={25}
        gap={80}
        logoHeight={40}
        grayscale={false}
        className="py-4"
      />

      {/* Bottom row - moving left to right */}
      <BrandMarquee
        brands={brands}
        direction="right"
        speed={30}
        gap={80}
        logoHeight={40}
        grayscale={false}
        className="py-4"
      />
    </div>
  )
}
