const cppCode = `#include <iostream>
#include <vector>
#include <algorithm>
#include <memory>

namespace DataStructures {
    template<typename T>
    class QuickSort {
    private:
        std::vector<T> data;

        // Partition function for quicksort
        int partition(int low, int high) {
            T pivot = data[high];
            int i = low - 1;

            for (int j = low; j < high; j++) {
                if (data[j] <= pivot) {
                    i++;
                    std::swap(data[i], data[j]);
                }
            }
            std::swap(data[i + 1], data[high]);
            return i + 1;
        }

        void quickSortRecursive(int low, int high) {
            if (low < high) {
                int pi = partition(low, high);
                quickSortRecursive(low, pi - 1);
                quickSortRecursive(pi + 1, high);
            }
        }

    public:
        QuickSort(std::vector<T> input) : data(std::move(input)) {}

        void sort() {
            if (!data.empty()) {
                quickSortRecursive(0, data.size() - 1);
            }
        }

        const std::vector<T>& getSorted() const {
            return data;
        }

        void display() const {
            std::cout << "Sorted array: ";
            for (const auto& element : data) {
                std::cout << element << " ";
            }
            std::cout << std::endl;
        }
    };
}

int main() {
    // Create a vector of integers
    std::vector<int> numbers = {64, 34, 25, 12, 22, 11, 90, 5};

    // Create QuickSort instance
    auto sorter = std::make_unique<DataStructures::QuickSort<int>>(numbers);

    std::cout << "Original array: ";
    for (int num : numbers) {
        std::cout << num << " ";
    }
    std::cout << std::endl;

    // Sort the array
    sorter->sort();
    sorter->display();

    return 0;
}`

export default function CodeBlockThemeDemo() {
  const [showLineNumbers, setShowLineNumbers] = useState(true)

  return (
    <div className="space-y-4">
      <div className="flex items-center gap-2">
        <Button
          variant={showLineNumbers ? "default" : "outline"}
          size="sm"
          onClick={() => setShowLineNumbers(!showLineNumbers)}
        >
          {showLineNumbers ? "Hide" : "Show"} Line Numbers
        </Button>
        <span className="text-muted-foreground text-sm">
          Custom C++ theme with adaptive light/dark variants
        </span>
      </div>

      <CodeBlock
        code={cppCode}
        language="cpp"
        filename="quicksort.cpp"
        showLineNumbers={showLineNumbers}
        adaptiveTheme={{
          dark: cppTheme,
          light: cppLightTheme,
        }}
      />
    </div>
  )
}
