"use client"

import { useEffect, useState } from "react"

import { MapboxPointer } from "@/registry/delta-ui/delta/mapbox-pointer"

const styles = [
  { name: "streets-v12", label: "Streets" },
  { name: "outdoors-v12", label: "Outdoors" },
  { name: "light-v11", label: "Light" },
  { name: "dark-v11", label: "Dark" },
  { name: "satellite-v9", label: "Satellite" },
  { name: "satellite-streets-v12", label: "Satellite Streets" },
  { name: "navigation-day-v1", label: "Navigation Day" },
  { name: "navigation-night-v1", label: "Navigation Night" },
] as const

export default function MapboxPointerInteractiveDemo() {
  const [currentStyleIndex, setCurrentStyleIndex] = useState(0)
  const token = process.env.NEXT_PUBLIC_MAPBOX_TOKEN || ""

  // Dublin, Ireland coordinates
  const latitude = 53.3498
  const longitude = -6.2603

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentStyleIndex((prevIndex) => (prevIndex + 1) % styles.length)
    }, 2000)

    return () => clearInterval(interval)
  }, [])

  if (!token) {
    return (
      <div className="flex h-96 items-center justify-center rounded-lg border">
        <p className="text-muted-foreground">Mapbox token not configured</p>
      </div>
    )
  }

  const currentStyle = styles[currentStyleIndex]

  return (
    <div className="w-full space-y-4">
      <div className="flex items-center justify-between">
        <div className="space-y-1">
          <h3 className="text-lg font-semibold">Style Cycling Demo</h3>
          <p className="text-muted-foreground text-sm">
            Automatically cycles through different Mapbox styles every 2 seconds
          </p>
        </div>
        <div className="bg-muted rounded-md px-3 py-1 text-sm font-medium">
          {currentStyle.label}
        </div>
      </div>

      <MapboxPointer
        latitude={latitude}
        longitude={longitude}
        zoom={12.8}
        mapboxToken={token}
        style={currentStyle.name}
        interactive={false}
        label="Dublin, Ireland"
        labelHref="https://en.wikipedia.org/wiki/Dublin"
        className="h-96 transition-all duration-500"
      />

      <div className="flex flex-wrap gap-2">
        {styles.map((style, index) => (
          <button
            key={style.name}
            onClick={() => setCurrentStyleIndex(index)}
            className={`rounded-md px-3 py-1 text-xs transition-colors ${
              index === currentStyleIndex
                ? "bg-primary text-primary-foreground"
                : "bg-muted text-muted-foreground hover:bg-muted/80"
            }`}
          >
            {style.label}
          </button>
        ))}
      </div>
    </div>
  )
}
