"use client"

import * as React from "react"
import Image from "next/image"
import {
  NavigationMenu,
  NavigationMenuContent,
  NavigationMenuItem,
  NavigationMenuLink,
  NavigationMenuList,
  NavigationMenuTrigger,
} from "@/registry/delta-ui/delta/navigation-menu"
import { cn } from "@/lib/utils"

const categories = [
  {
    title: "Business",
    items: [
      {
        title: "Travel & Tourism",
        href: "/business/travel",
        description: "Manage your travel bookings and itineraries",
        icon: "/images/isometric-icons/suitcase.png",
      },
      {
        title: "Transportation",
        href: "/business/transport",
        description: "Fleet management and logistics solutions",
        icon: "/images/isometric-icons/car.png",
      },
      {
        title: "Agriculture",
        href: "/business/agriculture", 
        description: "Farm management and livestock tracking",
        icon: "/images/isometric-icons/cow.png",
      },
    ],
  },
  {
    title: "Technology",
    items: [
      {
        title: "Cloud Services",
        href: "/tech/cloud",
        description: "Scalable cloud infrastructure and storage",
        icon: "/images/isometric-icons/cloud.png",
      },
      {
        title: "Media & Content",
        href: "/tech/media",
        description: "Photo and video management tools",
        icon: "/images/isometric-icons/camera.png",
      },
      {
        title: "Time Management",
        href: "/tech/time",
        description: "Scheduling and productivity applications",
        icon: "/images/isometric-icons/clock.png",
      },
    ],
  },
]

export default function NavigationMenuIsometricIconsDemo() {
  return (
    <NavigationMenu>
      <NavigationMenuList>
        {categories.map((category) => (
          <NavigationMenuItem key={category.title}>
            <NavigationMenuTrigger>{category.title}</NavigationMenuTrigger>
            <NavigationMenuContent>
              <ul className="grid w-[400px] gap-3 p-4 md:w-[500px] md:grid-cols-2 lg:w-[600px]">
                {category.items.map((item) => (
                  <ListItem
                    key={item.title}
                    title={item.title}
                    href={item.href}
                    icon={item.icon}
                  >
                    {item.description}
                  </ListItem>
                ))}
              </ul>
            </NavigationMenuContent>
          </NavigationMenuItem>
        ))}
        <NavigationMenuItem>
          <NavigationMenuLink href="#" className={navigationMenuTriggerStyle()}>
            <Image
              src="/images/isometric-icons/tree.png"
              alt="Documentation"
              width={20}
              height={20}
              className="mr-2"
            />
            Documentation
          </NavigationMenuLink>
        </NavigationMenuItem>
      </NavigationMenuList>
    </NavigationMenu>
  )
}

const ListItem = React.forwardRef<
  React.ElementRef<"a">,
  React.ComponentPropsWithoutRef<"a"> & {
    icon?: string
  }
>(({ className, title, children, icon, ...props }, ref) => {
  return (
    <li>
      <NavigationMenuLink asChild>
        <a
          ref={ref}
          className={cn(
            "block select-none space-y-1 rounded-md p-3 leading-none no-underline outline-none transition-colors hover:bg-accent hover:text-accent-foreground focus:bg-accent focus:text-accent-foreground",
            className
          )}
          {...props}
        >
          <div className="flex items-center gap-2">
            {icon && (
              <Image
                src={icon}
                alt={title}
                width={24}
                height={24}
                className="flex-shrink-0"
              />
            )}
            <div className="text-sm font-medium leading-none">{title}</div>
          </div>
          <p className="line-clamp-2 text-sm leading-snug text-muted-foreground ml-8">
            {children}
          </p>
        </a>
      </NavigationMenuLink>
    </li>
  )
})
ListItem.displayName = "ListItem"

function navigationMenuTriggerStyle() {
  return "group inline-flex h-10 w-max items-center justify-center rounded-md bg-background px-4 py-2 text-sm font-medium transition-colors hover:bg-accent hover:text-accent-foreground focus:bg-accent focus:text-accent-foreground focus:outline-none disabled:pointer-events-none disabled:opacity-50 data-[active]:bg-accent/50 data-[state=open]:bg-accent/50"
}
