/* Clare Theme - Light Mode */
html[data-theme="clare"] {
  --background: #faf4ed;
  --foreground: #575279;
  --card: #fffaf3;
  --card-foreground: #575279;
  --popover: #fffaf3;
  --popover-foreground: #575279;
  --primary: #907aa9;
  --primary-foreground: #faf4ed;
  --secondary: #f2e9e1;
  --secondary-foreground: #575279;
  --muted: #f2e9e1;
  --muted-foreground: #797593;
  --accent: #f2e9e1;
  --accent-foreground: #575279;
  --destructive: #b4637a;
  --destructive-foreground: #faf4ed;
  --border: #dfdad9;
  --input: #dfdad9;
  --ring: #907aa9;
  --chart-1: #d7827e;
  --chart-2: #ea9a97;
  --chart-3: #ea9d34;
  --chart-4: #286983;
  --chart-5: #56949f;
  --radius: 0.3rem;
  --sidebar: #fffaf3;
  --sidebar-foreground: #575279;
  --sidebar-primary: #907aa9;
  --sidebar-primary-foreground: #faf4ed;
  --sidebar-accent: #f2e9e1;
  --sidebar-accent-foreground: #575279;
  --sidebar-border: #dfdad9;
  --sidebar-ring: #907aa9;
  --shadow-x: 0px;
  --shadow-y: 4px;
  --shadow-blur: 8px;
  --shadow-spread: -1px;
  --shadow-opacity: 0.1;
  --shadow-color: hsl(0 0% 0%);
  --shadow-2xs: 0px 4px 8px -1px hsl(0 0% 0% / 0.05);
  --shadow-xs: 0px 4px 8px -1px hsl(0 0% 0% / 0.05);
  --shadow-sm: 0px 4px 8px -1px hsl(0 0% 0% / 0.10), 0px 1px 2px -2px hsl(0 0% 0% / 0.10);
  --shadow: 0px 4px 8px -1px hsl(0 0% 0% / 0.10), 0px 1px 2px -2px hsl(0 0% 0% / 0.10);
  --shadow-md: 0px 4px 8px -1px hsl(0 0% 0% / 0.10), 0px 2px 4px -2px hsl(0 0% 0% / 0.10);
  --shadow-lg: 0px 4px 8px -1px hsl(0 0% 0% / 0.10), 0px 4px 6px -2px hsl(0 0% 0% / 0.10);
  --shadow-xl: 0px 4px 8px -1px hsl(0 0% 0% / 0.10), 0px 8px 10px -2px hsl(0 0% 0% / 0.10);
  --shadow-2xl: 0px 4px 8px -1px hsl(0 0% 0% / 0.25);

  h1, h2, h3, h4, h5, h6 {
    color: #2aa198;
  }
}

/* Clare Theme - Dark Mode */
html[data-theme="clare"].dark {
  --background: #303446;
  --foreground: #c6d0f5;
  --card: #292c3c;
  --card-foreground: #c6d0f5;
  --popover: #292c3c;
  --popover-foreground: #c6d0f5;
  --primary: #ca9ee6;
  --primary-foreground: #303446;
  --secondary: #414559;
  --secondary-foreground: #9CA7D3;
  --muted: #414559;
  --muted-foreground: #a5adce;
  --accent: #414559;
  --accent-foreground: #c6d0f5;
  --destructive: #e78284;
  --destructive-foreground: #303446;
  --border: #474c66;
  --input: #414559;
  --ring: #ca9ee6;
  --chart-1: #ca9ee6;
  --chart-2: #8caaee;
  --chart-3: #a6d189;
  --chart-4: #e5c890;
  --chart-5: #ef9f76;
  --sidebar: #232634;
  --sidebar-foreground: #c6d0f5;
  --sidebar-primary: #ca9ee6;
  --sidebar-primary-foreground: #303446;
  --sidebar-accent: #414559;
  --sidebar-accent-foreground: #c6d0f5;
  --sidebar-border: #414559;
  --sidebar-ring: #ca9ee6;
  --shadow-x: 0px;
  --shadow-y: 4px;
  --shadow-blur: 8px;
  --shadow-spread: -1px;
  --shadow-opacity: 0.1;
  --shadow-color: hsl(0 0% 0%);
  --shadow-2xs: 0px 4px 8px -1px hsl(0 0% 0% / 0.05);
  --shadow-xs: 0px 4px 8px -1px hsl(0 0% 0% / 0.05);
  --shadow-sm: 0px 4px 8px -1px hsl(0 0% 0% / 0.10), 0px 1px 2px -2px hsl(0 0% 0% / 0.10);
  --shadow: 0px 4px 8px -1px hsl(0 0% 0% / 0.10), 0px 1px 2px -2px hsl(0 0% 0% / 0.10);
  --shadow-md: 0px 4px 8px -1px hsl(0 0% 0% / 0.10), 0px 2px 4px -2px hsl(0 0% 0% / 0.10);
  --shadow-lg: 0px 4px 8px -1px hsl(0 0% 0% / 0.10), 0px 4px 6px -2px hsl(0 0% 0% / 0.10);
  --shadow-xl: 0px 4px 8px -1px hsl(0 0% 0% / 0.10), 0px 8px 10px -2px hsl(0 0% 0% / 0.10);
  --shadow-2xl: 0px 4px 8px -1px hsl(0 0% 0% / 0.25);

  h1, h2, h3, h4, h5, h6 {
    color: #2aa198;
  }
}
