/* Dublin Theme */
html[data-theme="dublin"] {
  --background: oklch(0.9721 0.0158 110.5501);
  --foreground: oklch(0.5066 0.2501 271.8903);
  --card: oklch(0.9721 0.0158 110.5501);
  --card-foreground: oklch(0.5066 0.2501 271.8903);
  --popover: oklch(0.9721 0.0158 110.5501);
  --popover-foreground: oklch(0.5066 0.2501 271.8903);
  --primary: oklch(0.5066 0.2501 271.8903);
  --primary-foreground: oklch(1.0000 0 0);
  --secondary: oklch(1.0000 0 0);
  --secondary-foreground: oklch(0.5066 0.2501 271.8903);
  --muted: oklch(0.9189 0.0147 106.6853);
  --muted-foreground: oklch(0.5309 0.2319 271.8903);
  --accent: oklch(0.9168 0.0214 109.7161);
  --accent-foreground: oklch(0.5066 0.2501 271.8903);
  --destructive: oklch(0.7273 0.1891 27.3339);
  --destructive-foreground: oklch(1.0000 0 0);
  --border: oklch(0.5066 0.2501 271.8903);
  --input: oklch(0.9189 0.0147 106.6853);
  --ring: oklch(0.5066 0.2501 271.8903);
  --chart-1: oklch(0.6149 0.1394 244.9273);
  --chart-2: oklch(0.6444 0.1508 118.5967);
  --chart-3: oklch(0.6545 0.1340 85.7186);
  --chart-4: oklch(0.5808 0.1732 39.5003);
  --chart-5: oklch(0.5863 0.2064 27.1172);
  --sidebar: oklch(0.9189 0.0147 106.6853);
  --sidebar-foreground: oklch(0.5066 0.2501 271.8903);
  --sidebar-primary: oklch(0.5066 0.2501 271.8903);
  --sidebar-primary-foreground: oklch(1.0000 0 0);
  --sidebar-accent: oklch(0.9168 0.0214 109.7161);
  --sidebar-accent-foreground: oklch(0.5066 0.2501 271.8903);
  --sidebar-border: oklch(0.8710 0.0128 105.2570);
  --sidebar-ring: oklch(0.5066 0.2501 271.8903);
  --font-sans: ui-sans-serif, system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, 'Noto Sans', sans-serif;
  --font-serif: ui-serif, Georgia, Cambria, "Times New Roman", Times, serif;
  --font-mono: ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;
  --radius: 0rem;
  --shadow-2xs: 0 1px 3px 0px oklch(0.000 0.000 0.0 / 0.050);
  --shadow-xs: 0 1px 3px 0px oklch(0.000 0.000 0.0 / 0.050);
  --shadow-sm: 0 1px 3px 0px oklch(0.000 0.000 0.0 / 0.100), 0 1px 2px -1px oklch(0.000 0.000 0.0 / 0.100);
  --shadow: 0 1px 3px 0px oklch(0.000 0.000 0.0 / 0.100), 0 1px 2px -1px oklch(0.000 0.000 0.0 / 0.100);
  --shadow-md: 0 1px 3px 0px oklch(0.000 0.000 0.0 / 0.100), 0 2px 4px -1px oklch(0.000 0.000 0.0 / 0.100);
  --shadow-lg: 0 1px 3px 0px oklch(0.000 0.000 0.0 / 0.100), 0 4px 6px -1px oklch(0.000 0.000 0.0 / 0.100);
  --shadow-xl: 0 1px 3px 0px oklch(0.000 0.000 0.0 / 0.100), 0 8px 10px -1px oklch(0.000 0.000 0.0 / 0.100);
  --shadow-2xl: 0 1px 3px 0px oklch(0.000 0.000 0.0 / 0.250);
  --tracking-normal: 0em;
  --spacing: 0.25rem;
}

html[data-theme="dublin"] body,
html[data-theme="dublin"] .font-sans {
  font-family: ui-sans-serif, system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, 'Noto Sans', sans-serif;
}

