/* Galway (Claude) Theme */
html[data-theme="galway"] {
  --background: oklch(0.9818 0.0054 95.0986);
  --foreground: oklch(0.3438 0.0269 95.7226);
  --card: oklch(0.9818 0.0054 95.0986);
  --card-foreground: oklch(0.1908 0.0020 106.5859);
  --popover: oklch(1.0000 0 0);
  --popover-foreground: oklch(0.2671 0.0196 98.9390);
  --primary: oklch(0.6171 0.1375 39.0427);
  --primary-foreground: oklch(1.0000 0 0);
  --secondary: oklch(0.9245 0.0138 92.9892);
  --secondary-foreground: oklch(0.4334 0.0177 98.6048);
  --muted: oklch(0.9341 0.0153 90.2390);
  --muted-foreground: oklch(0.6059 0.0075 97.4233);
  --accent: oklch(0.9245 0.0138 92.9892);
  --accent-foreground: oklch(0.2671 0.0196 98.9390);
  --destructive: oklch(0.1908 0.0020 106.5859);
  --destructive-foreground: oklch(1.0000 0 0);
  --border: oklch(0.8847 0.0069 97.3627);
  --input: oklch(0.7621 0.0156 98.3528);
  --ring: oklch(0.6171 0.1375 39.0427);
  --chart-1: oklch(0.5583 0.1276 42.9956);
  --chart-2: oklch(0.6898 0.1581 290.4107);
  --chart-3: oklch(0.8816 0.0276 93.1280);
  --chart-4: oklch(0.8822 0.0403 298.1792);
  --chart-5: oklch(0.5608 0.1348 42.0584);
  --sidebar: oklch(0.9663 0.0080 98.8792);
  --sidebar-foreground: oklch(0.3590 0.0051 106.6524);
  --sidebar-primary: oklch(0.6171 0.1375 39.0427);
  --sidebar-primary-foreground: oklch(0.9881 0 0);
  --sidebar-accent: oklch(0.9245 0.0138 92.9892);
  --sidebar-accent-foreground: oklch(0.3250 0 0);
  --sidebar-border: oklch(0.9401 0 0);
  --sidebar-ring: oklch(0.7731 0 0);
  --surface: oklch(0.9700 0.0080 95.0000);
  --surface-foreground: oklch(0.4000 0.0300 100.0000);
  --font-sans: ui-sans-serif, system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, 'Noto Sans', sans-serif, 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol', 'Noto Color Emoji';
  --font-serif: ui-serif, Georgia, Cambria, "Times New Roman", Times, serif;
  --font-mono: ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;
  --radius: 0.5rem;
  --shadow-x: 0;
  --shadow-y: 1px;
  --shadow-blur: 3px;
  --shadow-spread: 0px;
  --shadow-opacity: 0.1;
  --shadow-color: oklch(0 0 0);
  --shadow-2xs: 0 1px 3px 0px oklch(0.000 0.000 0.0 / 0.050);
  --shadow-xs: 0 1px 3px 0px oklch(0.000 0.000 0.0 / 0.050);
  --shadow-sm: 0 1px 3px 0px oklch(0.000 0.000 0.0 / 0.100), 0 1px 2px -1px oklch(0.000 0.000 0.0 / 0.100);
  --shadow: 0 1px 3px 0px oklch(0.000 0.000 0.0 / 0.100), 0 1px 2px -1px oklch(0.000 0.000 0.0 / 0.100);
  --shadow-md: 0 1px 3px 0px oklch(0.000 0.000 0.0 / 0.100), 0 2px 4px -1px oklch(0.000 0.000 0.0 / 0.100);
  --shadow-lg: 0 1px 3px 0px oklch(0.000 0.000 0.0 / 0.100), 0 4px 6px -1px oklch(0.000 0.000 0.0 / 0.100);
  --shadow-xl: 0 1px 3px 0px oklch(0.000 0.000 0.0 / 0.100), 0 8px 10px -1px oklch(0.000 0.000 0.0 / 0.100);
  --shadow-2xl: 0 1px 3px 0px oklch(0.000 0.000 0.0 / 0.250);
  --tracking-normal: 0em;
  --spacing: 0.25rem;
}

