/* Kilkenny Theme */
html[data-theme="kilkenny"] {
  --background: #d6d3ce;
  --foreground: #2b2b2b;
  --card: #e0ded9;
  --card-foreground: #2b2b2b;
  --popover: #e0ded9;
  --popover-foreground: #2b2b2b;
  --primary: #2b2b2b;
  --primary-foreground: #f5f4f2;
  --secondary: #c9c6c1;
  --secondary-foreground: #2b2b2b;
  --muted: #c9c6c1;
  --muted-foreground: #5c5c5c;
  --accent: #bfbbb5;
  --accent-foreground: #2b2b2b;
  --destructive: #c53030;
  --destructive-foreground: #ffffff;
  --border: #2b2b2b;
  --input: #c9c6c1;
  --ring: #8a8680;
  --chart-1: #91c5ff;
  --chart-2: #3a81f6;
  --chart-3: #2563ef;
  --chart-4: #1a4eda;
  --chart-5: #1f3fad;
  --sidebar: #ccc9c4;
  --sidebar-foreground: #2b2b2b;
  --sidebar-primary: #2b2b2b;
  --sidebar-primary-foreground: #f5f4f2;
  --sidebar-accent: #bfbbb5;
  --sidebar-accent-foreground: #2b2b2b;
  --sidebar-border: #2b2b2b;
  --sidebar-ring: #8a8680;
  --font-sans: ui-sans-serif, system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, 'Noto Sans', sans-serif, 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol', 'Noto Color Emoji';
  --font-serif: ui-serif, Georgia, Cambria, "Times New Roman", Times, serif;
  --font-mono: ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;
  --radius: 0.25rem;
  --shadow-x: 0;
  --shadow-y: 1px;
  --shadow-blur: 3px;
  --shadow-spread: 0px;
  --shadow-opacity: 0.1;
  --shadow-color: oklch(0 0 0);
  --shadow-2xs: 0 1px 3px 0px hsl(0 0% 0% / 0.05);
  --shadow-xs: 0 1px 3px 0px hsl(0 0% 0% / 0.05);
  --shadow-sm: 0 1px 3px 0px hsl(0 0% 0% / 0.10), 0 1px 2px -1px hsl(0 0% 0% / 0.10);
  --shadow: 0 1px 3px 0px hsl(0 0% 0% / 0.10), 0 1px 2px -1px hsl(0 0% 0% / 0.10);
  --shadow-md: 0 1px 3px 0px hsl(0 0% 0% / 0.10), 0 2px 4px -1px hsl(0 0% 0% / 0.10);
  --shadow-lg: 0 1px 3px 0px hsl(0 0% 0% / 0.10), 0 4px 6px -1px hsl(0 0% 0% / 0.10);
  --shadow-xl: 0 1px 3px 0px hsl(0 0% 0% / 0.10), 0 8px 10px -1px hsl(0 0% 0% / 0.10);
  --shadow-2xl: 0 1px 3px 0px hsl(0 0% 0% / 0.25);
  --surface: #e0ded9;
  --surface-foreground: #2b2b2b;
  --tracking-normal: 0em;
  --spacing: 0.25rem;
}

html[data-theme="kilkenny"].dark {
  --background: #1a1918;
  --foreground: #e8e6e3;
  --card: #252422;
  --card-foreground: #e8e6e3;
  --popover: #2f2d2b;
  --popover-foreground: #e8e6e3;
  --primary: #e8e6e3;
  --primary-foreground: #1a1918;
  --secondary: #3a3836;
  --secondary-foreground: #e8e6e3;
  --muted: #3a3836;
  --muted-foreground: #a09d99;
  --accent: #4a4745;
  --accent-foreground: #e8e6e3;
  --destructive: #f87171;
  --destructive-foreground: #1a1918;
  --border: #3a3836;
  --input: #4a4745;
  --ring: #6b6864;
  --chart-1: #91c5ff;
  --chart-2: #3a81f6;
  --chart-3: #2563ef;
  --chart-4: #1a4eda;
  --chart-5: #1f3fad;
  --sidebar: #252422;
  --sidebar-foreground: #e8e6e3;
  --sidebar-primary: #3a81f6;
  --sidebar-primary-foreground: #e8e6e3;
  --sidebar-accent: #3a3836;
  --sidebar-accent-foreground: #e8e6e3;
  --sidebar-border: #3a3836;
  --sidebar-ring: #5c5a57;
  --font-sans: ui-sans-serif, system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, 'Noto Sans', sans-serif, 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol', 'Noto Color Emoji';
  --font-serif: ui-serif, Georgia, Cambria, "Times New Roman", Times, serif;
  --font-mono: ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;
  --radius: 0.25rem;
  --shadow-x: 0;
  --shadow-y: 1px;
  --shadow-blur: 3px;
  --shadow-spread: 0px;
  --shadow-opacity: 0.1;
  --shadow-color: oklch(0 0 0);
  --shadow-2xs: 0 1px 3px 0px hsl(0 0% 0% / 0.05);
  --shadow-xs: 0 1px 3px 0px hsl(0 0% 0% / 0.05);
  --shadow-sm: 0 1px 3px 0px hsl(0 0% 0% / 0.10), 0 1px 2px -1px hsl(0 0% 0% / 0.10);
  --shadow: 0 1px 3px 0px hsl(0 0% 0% / 0.10), 0 1px 2px -1px hsl(0 0% 0% / 0.10);
  --shadow-md: 0 1px 3px 0px hsl(0 0% 0% / 0.10), 0 2px 4px -1px hsl(0 0% 0% / 0.10);
  --shadow-lg: 0 1px 3px 0px hsl(0 0% 0% / 0.10), 0 4px 6px -1px hsl(0 0% 0% / 0.10);
  --shadow-xl: 0 1px 3px 0px hsl(0 0% 0% / 0.10), 0 8px 10px -1px hsl(0 0% 0% / 0.10);
  --shadow-2xl: 0 1px 3px 0px hsl(0 0% 0% / 0.25);
  --surface: #252422;
  --surface-foreground: #e8e6e3;
}

html[data-theme="kilkenny"] body,
html[data-theme="kilkenny"] .font-sans {
  font-family: ui-sans-serif, system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, 'Noto Sans', sans-serif;
}
