/* Sligo (Claude) Theme */
html[data-theme="sligo"] {
  --radius: 0.3rem;
  --background: oklch(0.987 0.026 102.2);
  --foreground: oklch(0.182 0.050 46.9);
  --card: oklch(0.987 0.026 102.2);
  --card-foreground: oklch(0.182 0.050 46.9);
  --popover: oklch(0.990 0.009 100.0);
  --popover-foreground: oklch(0.182 0.050 46.9);
  --primary: oklch(0.182 0.050 46.9);
  --primary-foreground: oklch(0.990 0.009 100.0);
  --secondary: oklch(0.810 0.120 62.4);
  --secondary-foreground: oklch(0.182 0.050 46.9);
  --muted: oklch(0.950 0.020 100.6);
  --muted-foreground: oklch(0.556 0.030 44.5);
  --accent: oklch(0.810 0.120 62.4);
  --accent-foreground: oklch(0.182 0.050 46.9);
  --destructive: oklch(0.583 0.239 28.5);
  --destructive-foreground: oklch(0.990 0.009 100.0);
  --border: oklch(0.810 0.120 62.4);
  --input: oklch(0.810 0.120 62.4);
  --ring: oklch(0.182 0.050 46.9);
  --chart-1: oklch(0.647 0.217 36.9);
  --chart-2: oklch(0.605 0.107 184.2);
  --chart-3: oklch(0.397 0.070 227.2);
  --chart-4: oklch(0.829 0.171 81.0);
  --chart-5: oklch(0.772 0.173 65.4);
  --sidebar: oklch(0.978 0.020 102.5);
  --sidebar-foreground: oklch(0.182 0.050 46.9);
  --sidebar-primary: oklch(0.182 0.050 46.9);
  --sidebar-primary-foreground: oklch(0.990 0.009 100.0);
  --sidebar-accent: oklch(0.960 0.022 100.8);
  --sidebar-accent-foreground: oklch(0.182 0.050 46.9);
  --sidebar-border: oklch(0.920 0.025 100.0);
  --sidebar-ring: oklch(0.182 0.050 46.9);
  --surface: oklch(0.978 0.020 102.5);
  --surface-foreground: oklch(0.182 0.050 46.9);
  --code: oklch(0.978 0.020 102.5);
  --code-foreground: oklch(0.182 0.050 46.9);
  --code-highlight: oklch(0.960 0.022 100.8);
  --code-number: oklch(0.556 0.030 44.5);
  --selection: oklch(0.182 0.050 46.9);
  --selection-foreground: oklch(0.990 0.009 100.0);
}

html[data-theme="sligo"].dark {
  --radius: 0.5rem;
  --background: oklch(0.176 0.025 42.3);
  --foreground: oklch(0.784 0.127 59.7);
  --card: oklch(0.234 0.036 42.5);
  --card-foreground: oklch(0.784 0.127 59.7);
  --popover: oklch(0.234 0.036 42.5);
  --popover-foreground: oklch(0.784 0.127 59.7);
  --primary: oklch(0.784 0.127 59.7);
  --primary-foreground: oklch(0.234 0.036 42.5);
  --secondary: oklch(0.289 0.044 46.2);
  --secondary-foreground: oklch(0.784 0.127 59.7);
  --muted: oklch(0.329 0.058 46.6);
  --muted-foreground: oklch(0.650 0.100 58.5);
  --accent: oklch(0.234 0.036 42.5);
  --accent-foreground: oklch(0.784 0.127 59.7);
  --destructive: oklch(0.577 0.215 27.3);
  --destructive-foreground: oklch(1.000 0.000 0.0);
  --border: oklch(0.364 0.050 47.3);
  --input: oklch(0.364 0.050 47.3);
  --ring: oklch(0.784 0.127 59.7);
  --chart-1: oklch(0.784 0.127 59.7);
  --chart-2: oklch(0.826 0.107 64.1);
  --chart-3: oklch(0.864 0.086 66.9);
  --chart-4: oklch(0.904 0.064 70.7);
  --chart-5: oklch(0.945 0.042 77.6);
  --sidebar: oklch(0.234 0.036 42.5);
  --sidebar-foreground: oklch(0.784 0.127 59.7);
  --sidebar-primary: oklch(0.784 0.127 59.7);
  --sidebar-primary-foreground: oklch(0.234 0.036 42.5);
  --sidebar-accent: oklch(0.289 0.044 46.2);
  --sidebar-accent-foreground: oklch(0.784 0.127 59.7);
  --sidebar-border: oklch(0.329 0.058 46.6);
  --sidebar-ring: oklch(0.784 0.127 59.7);
  --surface: oklch(0.234 0.036 42.5);
  --surface-foreground: oklch(0.784 0.127 59.7);
  --code: oklch(0.234 0.036 42.5);
  --code-foreground: oklch(0.784 0.127 59.7);
  --code-highlight: oklch(0.289 0.044 46.2);
  --code-number: oklch(0.826 0.107 64.1);
  --selection: oklch(0.784 0.127 59.7);
  --selection-foreground: oklch(0.234 0.036 42.5);
}
