/* Wexford (Gruvbox) Theme */
html[data-theme="wexford"] {
  --background: #ede3c0;
  --foreground: #303030;
  --card: #f0ead1;
  --card-foreground: #303030;
  --popover: #f0ead1;
  --popover-foreground: #303030;
  --primary: #9e0000;
  --primary-foreground: #f9f6ec;
  --secondary: #d8cfb6;
  --secondary-foreground: #303030;
  --muted: #e0d6be;
  --muted-foreground: #625950;
  --accent: #ebd8ad;
  --accent-foreground: #7a431f;
  --destructive: #c32822;
  --destructive-foreground: #f9f6ec;
  --border: #c9bea6;
  --input: #c9bea6;
  --ring: #9e0000;
  --chart-1: #9e0000;
  --chart-2: #b8ba26;
  --chart-3: #fbba23;
  --chart-4: #369671;
  --chart-5: #d85f0e;
  --sidebar: #e6dbbc;
  --sidebar-foreground: #303030;
  --sidebar-primary: #9e0000;
  --sidebar-primary-foreground: #f9f6ec;
  --sidebar-accent: #ebd8ad;
  --sidebar-accent-foreground: #7a431f;
  --sidebar-border: #c9bea6;
  --sidebar-ring: #d85f0e;
  --surface: #f2e8cc;
  --surface-foreground: #3c3836;
  --font-sans: ui-sans-serif, system-ui, -apple-system, BlinkMacMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue",
    Arial, "Noto Sans", sans-serif;
  --font-serif: ui-serif, Georgia, Cambria, "Times New Roman", Times, serif;
  --font-mono: ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;
  --radius: 0.25rem;
  --shadow-x: 0;
  --shadow-y: 1px;
  --shadow-blur: 3px;
  --shadow-spread: 0px;
  --shadow-opacity: 0.1;
  --shadow-color: oklch(0 0 0);
  --shadow-2xs: 0 1px 3px 0px hsl(0 0% 0% / 0.05);
  --shadow-xs: 0 1px 3px 0px hsl(0 0% 0% / 0.05);
  --shadow-sm: 0 1px 3px 0px hsl(0 0% 0% / 0.10), 0 1px 2px -1px hsl(0 0% 0% / 0.10);
  --shadow: 0 1px 3px 0px hsl(0 0% 0% / 0.10), 0 1px 2px -1px hsl(0 0% 0% / 0.10);
  --shadow-md: 0 1px 3px 0px hsl(0 0% 0% / 0.10), 0 2px 4px -1px hsl(0 0% 0% / 0.10);
  --shadow-lg: 0 1px 3px 0px hsl(0 0% 0% / 0.10), 0 4px 6px -1px hsl(0 0% 0% / 0.10);
  --shadow-xl: 0 1px 3px 0px hsl(0 0% 0% / 0.10), 0 8px 10px -1px hsl(0 0% 0% / 0.10);
  --shadow-2xl: 0 1px 3px 0px hsl(0 0% 0% / 0.25);
  --tracking-normal: 0em;
  --spacing: 0.25rem;
}

html[data-theme="wexford"].dark {
  --background: #32302f;
  --foreground: #e0d6be;
  --card: #3b3735;
  --card-foreground: #e0d6be;
  --popover: #3b3735;
  --popover-foreground: #e0d6be;
  --primary: #fe811b;
  --primary-foreground: #282625;
  --secondary: #504b49;
  --secondary-foreground: #d8cfb6;
  --muted: #504b49;
  --muted-foreground: #9d927b;
  --accent: #53402d;
  --accent-foreground: #feab67;
  --destructive: #fb4632;
  --destructive-foreground: #282625;
  --border: #615a56;
  --input: #615a56;
  --ring: #fe811b;
  --chart-1: #fe811b;
  --chart-2: #dddd36;
  --chart-3: #fed471;
  --chart-4: #55b458;
  --chart-5: #fb4632;
  --sidebar: #2a2827;
  --sidebar-foreground: #e0d6be;
  --sidebar-primary: #fe811b;
  --sidebar-primary-foreground: #282625;
  --sidebar-accent: #53402d;
  --sidebar-accent-foreground: #feab67;
  --sidebar-border: #615a56;
  --sidebar-ring: #fe811b;
  --surface: #2a2827;
  --surface-foreground: #d5c4a1;
  --font-sans: ui-sans-serif, system-ui, -apple-system, BlinkMacMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue",
    Arial, "Noto Sans", sans-serif;
  --font-serif: ui-serif, Georgia, Cambria, "Times New Roman", Times, serif;
  --font-mono: ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;
  --radius: 0.25rem;
  --shadow-x: 0;
  --shadow-y: 1px;
  --shadow-blur: 3px;
  --shadow-spread: 0px;
  --shadow-opacity: 0.1;
  --shadow-color: oklch(0 0 0);
  --shadow-2xs: 0 1px 3px 0px hsl(0 0% 0% / 0.05);
  --shadow-xs: 0 1px 3px 0px hsl(0 0% 0% / 0.05);
  --shadow-sm: 0 1px 3px 0px hsl(0 0% 0% / 0.10), 0 1px 2px -1px hsl(0 0% 0% / 0.10);
  --shadow: 0 1px 3px 0px hsl(0 0% 0% / 0.10), 0 1px 2px -1px hsl(0 0% 0% / 0.10);
  --shadow-md: 0 1px 3px 0px hsl(0 0% 0% / 0.10), 0 2px 4px -1px hsl(0 0% 0% / 0.10);
  --shadow-lg: 0 1px 3px 0px hsl(0 0% 0% / 0.10), 0 4px 6px -1px hsl(0 0% 0% / 0.10);
  --shadow-xl: 0 1px 3px 0px hsl(0 0% 0% / 0.10), 0 8px 10px -1px hsl(0 0% 0% / 0.10);
  --shadow-2xl: 0 1px 3px 0px hsl(0 0% 0% / 0.25);
}

html[data-theme="wexford"] body,
html[data-theme="wexford"] .font-sans {
  font-family: ui-sans-serif, system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, 'Noto Sans', sans-serif;
}

html[data-theme="wexford"] h1 {
  color: #E8B547;
  font-weight: bold;
}


html[data-theme="wexford"] h2 {
  color: #BC3826;
  font-weight: bold;
}

html[data-theme="wexford"] h3 {
  color: #969732;
  font-weight: bold;
}

html[data-theme="wexford"] h4 {
  color: #729C6E;
  font-weight: bold;
}

html[data-theme="wexford"] h5 {
  color: #558488;
  font-weight: bold;
}
