"use client"

import { useState } from "react"
import { BackdropGradient } from "@/components/ui/backdrop-gradient"
import { Slider } from "@/components/ui/slider"

export default function BackdropGradientDemo() {
  // Simple state for the demo controls
  const [blurAmount, setBlurAmount] = useState(70)
  const [gradientIntensity, setGradientIntensity] = useState(0.7)

  // Sample image URL - a colorful image works best to demonstrate the effect
  const imageUrl = "https://placehold.co/600x400/e91e63/ffffff"

  // Handle slider changes
  function handleBlurChange(value) {
    if (Array.isArray(value) && value.length > 0) {
      setBlurAmount(value[0])
    }
  }

  function handleIntensityChange(value) {
    if (Array.isArray(value) && value.length > 0) {
      setGradientIntensity(value[0])
    }
  }

  return (
    <div className="space-y-8">
      {/* Minimal BackdropGradient example */}
      <div className="flex justify-center">
        <BackdropGradient
          src={imageUrl}
          alt="Colorful demo image"
          width={600}
          height={400}
          blurAmount={blurAmount}
          gradientIntensity={gradientIntensity}
          className="w-full max-w-[600px]"
        />
      </div>

      {/* Simple controls */}
      <div className="max-w-[600px] mx-auto space-y-6">
        <div className="space-y-2">
          <div className="flex justify-between">
            <span className="text-sm font-medium">Blur: {blurAmount}px</span>
            <span className="text-sm text-muted-foreground">0-150px</span>
          </div>
          <Slider min={0} max={150} step={1} value={[blurAmount]} onValueChange={handleBlurChange} />
        </div>

        <div className="space-y-2">
          <div className="flex justify-between">
            <span className="text-sm font-medium">Intensity: {gradientIntensity.toFixed(1)}</span>
            <span className="text-sm text-muted-foreground">0-1</span>
          </div>
          <Slider min={0} max={1} step={0.1} value={[gradientIntensity]} onValueChange={handleIntensityChange} />
        </div>
      </div>
    </div>
  )
}
