"use client"

import React from "react";
import { FileInput } from "@/registry/inputs/file-input";

export default function FileInputMultipleDemo() {
  return (
    <div className="w-full max-w-md mx-auto p-4">
      <FileInput
        label="Upload Images"
        name="images"
        multiple={true}
        maxFiles={5}
        accept="image/*,.jpg,.jpeg,.png,.gif,.webp"
        description="Upload multiple images (up to 5 files)"
        hint="Drag & drop to add files progressively, or browse to replace selection"
        dropzoneText="Drop multiple images here or click to browse"
        maxSize={10 * 1024 * 1024} // 10MB per file
        showIcons={true}
        showPreviews={true}
      />
    </div>
  );
}
