"use client"

import React from "react";
import { FileInput } from "@/registry/inputs/file-input";

export default function FileInputPillDemo() {
  return (
    <div className="w-full max-w-md mx-auto p-4">
      <FileInput
        label="Upload Profile Photo"
        name="profilePhoto"
        variant="pill"
        accept="image/*"
        description="Pill variant with modern styling"
        hint="Upload a profile photo in JPG, PNG, or GIF format"
        dropzoneText="Drop your profile photo here"
        maxSize={5 * 1024 * 1024} // 5MB
      />
    </div>
  );
}
