"use client"

import React from "react";
import { OtpInput } from "@/registry/inputs/otp-input";

export default function OtpInputShortDemo() {
  return (
    <div className="w-full max-w-md mx-auto p-4">
      <OtpInput
        label="PIN Code"
        name="pinCode"
        length={4}
        required
        hint="Enter your 4-digit PIN"
        description="Use your secure 4-digit PIN to access your account"
        onComplete={(code) => console.log('PIN entered:', code)}
      />
    </div>
  );
}
