import { cn } from "@/lib/utils"

export interface ProgressiveBlurProps {
  className?: string
  position?: "top" | "bottom"
  height?: string // e.g. "150px"
  blurAmount?: string // e.g. "4px" or "sm" | "md" | "lg"
  enabled?: boolean
}

export default function ProgressiveBlur({
  className = "",
  position = "top",
  height = "120px",
  blurAmount = "8px",
  enabled = true,
}: ProgressiveBlurProps) {
  const isTop = position === "top"

  const blurLayers = [
    { blur: "1px", opacity: 0.2 },
    { blur: "3px", opacity: 0.3 },
    { blur: "6px", opacity: 0.4 },
    { blur: "10px", opacity: 0.3 },
    { blur: blurAmount, opacity: 0.2 },
  ]

  if (!enabled) {
    return null
  }

  return (
    <div
      className={cn("pointer-events-none absolute left-0 w-full select-none", isTop ? "top-0" : "bottom-0", className)}
      style={{ height }}
    >
      <div className={cn("absolute inset-0 bg-background", isTop ? "mask-t-from-0%" : "mask-b-from-0%")} />
      {blurLayers.map((layer, index) => (
        <div
          key={index}
          className={cn("absolute inset-0", isTop ? "mask-t-from-0%" : "mask-b-from-0%")}
          style={{
            backdropFilter: `blur(${layer.blur})`,
            opacity: layer.opacity,
          }}
        />
      ))}
    </div>
  )
}
