"use client";

import React, { useState } from 'react';

interface RetroButtonProps {
  children?: React.ReactNode;
  icon?: React.ComponentType<{ className?: string }>;
  onClick?: () => void;
  disabled?: boolean;
  size?: 'sm' | 'md' | 'lg';
  variant?: 'default' | 'primary' | 'secondary';
  className?: string;
}z

export default function RetroButton({
  children,
  icon: Icon,
  onClick,
  disabled = false,
  size = 'md',
  variant = 'default',
  className = ''
}: RetroButtonProps) {
  const [isPressed, setIsPressed] = useState(false);

  const sizeClasses = {
    sm: 'w-8 h-8 text-xs',
    md: 'w-12 h-12 text-sm',
    lg: 'w-16 h-16 text-base'
  };

  const variantClasses = {
    default: {
      bg: 'bg-gray-200 dark:bg-gray-700',
      border: 'border-gray-400 dark:border-gray-500',
      text: 'text-gray-900 dark:text-gray-100'
    },
    primary: {
      bg: 'bg-blue-200 dark:bg-blue-800',
      border: 'border-blue-400 dark:border-blue-600',
      text: 'text-blue-900 dark:text-blue-100'
    },
    secondary: {
      bg: 'bg-slate-200 dark:bg-slate-700',
      border: 'border-slate-400 dark:border-slate-500',
      text: 'text-slate-900 dark:text-slate-100'
    }
  };

  const currentVariant = variantClasses[variant];

  // Neobrutalist multi-layer shadow offsets
  const baseShadow = 'shadow-[6px_6px_0_rgb(0,0,0),-6px_-6px_0_rgb(255,255,255)]';
  const pressedShadow = 'shadow-[inset_6px_6px_0_rgb(0,0,0),inset_-6px_-6px_0_rgb(255,255,255)]';

  const handleMouseDown = () => {
    if (!disabled) setIsPressed(true);
  };
  const handleMouseUp = () => setIsPressed(false);
  const handleMouseLeave = () => setIsPressed(false);
  const handleClick = () => {
    if (!disabled && onClick) onClick();
  };

  return (
    <button
      className={`
        ${sizeClasses[size]}
        ${currentVariant.bg}
        ${currentVariant.border}
        ${currentVariant.text}
        border-4
        ${isPressed ? pressedShadow + ' translate-x-[2px] translate-y-[2px]' : baseShadow}
        ${disabled ? 'opacity-50 cursor-not-allowed' : 'cursor-pointer'}
        flex items-center justify-center
        font-bold
        select-none
        transition-all duration-100
        ${className}
      `.trim()}
      onMouseDown={handleMouseDown}
      onMouseUp={handleMouseUp}
      onMouseLeave={handleMouseLeave}
      onClick={handleClick}
      disabled={disabled}
    >
      {Icon && (
        <Icon className={`
          ${size === 'sm' ? 'w-3 h-3' : size === 'md' ? 'w-4 h-4' : 'w-5 h-5'}
          ${children ? 'mr-1' : ''}
        `} />
      )}
      {children}
    </button>
  );
}
