"use client"

import React from "react";
import { TextareaInput } from "@/registry/inputs/textarea-input";

export default function TextareaInputMaxDemo() {
  return (
    <div className="w-full max-w-lg mx-auto p-4">
      <TextareaInput
        label="Detailed Product Review"
        name="detailedReview"
        maxLength={500}
        showCharacterCount={true}
        rows={6}
        placeholder="Write a comprehensive review (max 500 characters)..."
        description="Extended character limit with increased height"
        hint="Share your detailed experience with this product"
        defaultValue="This product has completely transformed my daily workflow and exceeded all my expectations! The build quality is exceptional with attention to every detail. The user interface is intuitive and the customer support team responded within hours. I've been using it for three months now and haven't encountered a single issue. The value for money is outstanding compared to similar products in the market. I would definitely recommend this to anyone looking for a reliable solution."
      />
    </div>
  );
}
